import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as h,a as e,b as n,e as d,d as i,r as s}from"./app.8e263a3f.js";const c={},o=d('<p>\u2003\u2003\u65B0\u73AF\u5883\u4FDD\u62A4\u6CD5\u548C\u65B0\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u65BD\u884C\u4EE5\u6765\uFF0C\u5173\u4E8E\u5EFA\u8BBE\u5355\u4F4D\u672A\u4F9D\u6CD5\u62A5\u6279\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\uFF0C\u6216\u8005\u672A\u4F9D\u7167\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u7B2C\u4E8C\u5341\u56DB\u6761\u7684\u89C4\u5B9A\u91CD\u65B0\u62A5\u6279\u6216\u8005\u62A5\u8BF7\u91CD\u65B0\u5BA1\u6838\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\uFF0C\u64C5\u81EA\u5F00\u5DE5\u5EFA\u8BBE\uFF08\u4EE5\u4E0B\u7B80\u79F0\u201C\u672A\u6279\u5148\u5EFA\u201D\uFF09\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A\uFF0C\u5728\u6CD5\u5F8B\u9002\u7528\u3001\u8FFD\u6EAF\u671F\u9650\u4EE5\u53CA\u540E\u7EED\u529E\u7406\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u624B\u7EED\u7B49\u65B9\u9762\uFF0C\u5B9E\u8DF5\u4E2D\u5B58\u5728\u4E0D\u540C\u4E89\u8BAE\u3002\u7ECF\u7814\u7A76\uFF0C\u73B0\u5C31\u6709\u5173\u6CD5\u5F8B\u6CD5\u89C4\u7684\u9002\u7528\u95EE\u9898\u63D0\u51FA\u4EE5\u4E0B\u610F\u89C1\u3002</p><h2 id="\u4E00\u3001\u5173\u4E8E-\u672A\u6279\u5148\u5EFA-\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u7684\u6CD5\u5F8B\u9002\u7528" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u5173\u4E8E-\u672A\u6279\u5148\u5EFA-\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u7684\u6CD5\u5F8B\u9002\u7528" aria-hidden="true">#</a> \u4E00\u3001\u5173\u4E8E\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u7684\u6CD5\u5F8B\u9002\u7528</h2><h3 id="\u4E00-\u76F8\u5173\u6CD5\u5F8B\u89C4\u5B9A" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u76F8\u5173\u6CD5\u5F8B\u89C4\u5B9A" aria-hidden="true">#</a> \uFF08\u4E00\uFF09\u76F8\u5173\u6CD5\u5F8B\u89C4\u5B9A</h3><p>\u2003\u20032002\u5E74\u516C\u5E03\u7684\u539F\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\uFF08\u81EA2003\u5E749\u67081\u65E5\u8D77\u65BD\u884C\uFF09\u7B2C\u4E09\u5341\u4E00\u6761\u7B2C\u4E00\u6B3E\u3001\u7B2C\u4E8C\u6B3E\u5206\u522B\u89C4\u5B9A\uFF1A\u201C\u5EFA\u8BBE\u5355\u4F4D\u672A\u4F9D\u6CD5\u62A5\u6279\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\uFF0C\u6216\u8005\u672A\u4F9D\u7167\u672C\u6CD5\u7B2C\u4E8C\u5341\u56DB\u6761\u7684\u89C4\u5B9A\u91CD\u65B0\u62A5\u6279\u6216\u8005\u62A5\u8BF7\u91CD\u65B0\u5BA1\u6838\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\uFF0C\u64C5\u81EA\u5F00\u5DE5\u5EFA\u8BBE\u7684\uFF0C\u7531\u6709\u6743\u5BA1\u6279\u8BE5\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u7684\u73AF\u5883\u4FDD\u62A4\u884C\u653F\u4E3B\u7BA1\u90E8\u95E8\u8D23\u4EE4\u505C\u6B62\u5EFA\u8BBE\uFF0C\u9650\u671F\u8865\u529E\u624B\u7EED\uFF1B\u903E\u671F\u4E0D\u8865\u529E\u624B\u7EED\u7684\uFF0C\u53EF\u4EE5\u5904\u4E94\u4E07\u5143\u4EE5\u4E0A\u4E8C\u5341\u4E07\u5143\u4EE5\u4E0B\u7684\u7F5A\u6B3E\uFF0C\u5BF9\u5EFA\u8BBE\u5355\u4F4D\u76F4\u63A5\u8D1F\u8D23\u7684\u4E3B\u7BA1\u4EBA\u5458\u548C\u5176\u4ED6\u76F4\u63A5\u8D23\u4EFB\u4EBA\u5458\uFF0C\u4F9D\u6CD5\u7ED9\u4E88\u884C\u653F\u5904\u5206\u3002<br> \u2003\u2003\u201C\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u672A\u7ECF\u6279\u51C6\u6216\u8005\u672A\u7ECF\u539F\u5BA1\u6279\u90E8\u95E8\u91CD\u65B0\u5BA1\u6838\u540C\u610F\uFF0C\u5EFA\u8BBE\u5355\u4F4D\u64C5\u81EA\u5F00\u5DE5\u5EFA\u8BBE\u7684\uFF0C\u7531\u6709\u6743\u5BA1\u6279\u8BE5\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u7684\u73AF\u5883\u4FDD\u62A4\u884C\u653F\u4E3B\u7BA1\u90E8\u95E8\u8D23\u4EE4\u505C\u6B62\u5EFA\u8BBE\uFF0C\u53EF\u4EE5\u5904\u4E94\u4E07\u5143\u4EE5\u4E0A\u4E8C\u5341\u4E07\u5143\u4EE5\u4E0B\u7684\u7F5A\u6B3E\uFF0C\u5BF9\u5EFA\u8BBE\u5355\u4F4D\u76F4\u63A5\u8D1F\u8D23\u7684\u4E3B\u7BA1\u4EBA\u5458\u548C\u5176\u4ED6\u76F4\u63A5\u8D23\u4EFB\u4EBA\u5458\uFF0C\u4F9D\u6CD5\u7ED9\u4E88\u884C\u653F\u5904\u5206\u3002\u201D<br> \u2003\u20032014\u5E74\u4FEE\u8BA2\u7684\u65B0\u73AF\u5883\u4FDD\u62A4\u6CD5\uFF08\u81EA2015\u5E741\u67081\u65E5\u8D77\u65BD\u884C\uFF09\u7B2C\u516D\u5341\u4E00\u6761\u89C4\u5B9A\uFF1A\u201C\u5EFA\u8BBE\u5355\u4F4D\u672A\u4F9D\u6CD5\u63D0\u4EA4\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u6216\u8005\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u672A\u7ECF\u6279\u51C6\uFF0C\u64C5\u81EA\u5F00\u5DE5\u5EFA\u8BBE\u7684\uFF0C\u7531\u8D1F\u6709\u73AF\u5883\u4FDD\u62A4\u76D1\u7763\u7BA1\u7406\u804C\u8D23\u7684\u90E8\u95E8\u8D23\u4EE4\u505C\u6B62\u5EFA\u8BBE\uFF0C\u5904\u4EE5\u7F5A\u6B3E\uFF0C\u5E76\u53EF\u4EE5\u8D23\u4EE4\u6062\u590D\u539F\u72B6\u3002\u201D<br> \u2003\u20032016\u5E74\u4FEE\u6B63\u7684\u65B0\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\uFF08\u81EA2016\u5E749\u67081\u65E5\u8D77\u65BD\u884C\uFF09\u7B2C\u4E09\u5341\u4E00\u6761\u89C4\u5B9A\uFF1A\u201C\u5EFA\u8BBE\u5355\u4F4D\u672A\u4F9D\u6CD5\u62A5\u6279\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\uFF0C\u6216\u8005\u672A\u4F9D\u7167\u672C\u6CD5\u7B2C\u4E8C\u5341\u56DB\u6761\u7684\u89C4\u5B9A\u91CD\u65B0\u62A5\u6279\u6216\u8005\u62A5\u8BF7\u91CD\u65B0\u5BA1\u6838\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\uFF0C\u64C5\u81EA\u5F00\u5DE5\u5EFA\u8BBE\u7684\uFF0C\u7531\u53BF\u7EA7\u4EE5\u4E0A\u73AF\u5883\u4FDD\u62A4\u884C\u653F\u4E3B\u7BA1\u90E8\u95E8\u8D23\u4EE4\u505C\u6B62\u5EFA\u8BBE\uFF0C\u6839\u636E\u8FDD\u6CD5\u60C5\u8282\u548C\u5371\u5BB3\u540E\u679C\uFF0C\u5904\u5EFA\u8BBE\u9879\u76EE\u603B\u6295\u8D44\u989D\u767E\u5206\u4E4B\u4E00\u4EE5\u4E0A\u767E\u5206\u4E4B\u4E94\u4EE5\u4E0B\u7684\u7F5A\u6B3E\uFF0C\u5E76\u53EF\u4EE5\u8D23\u4EE4\u6062\u590D\u539F\u72B6\uFF1B\u5BF9\u5EFA\u8BBE\u5355\u4F4D\u76F4\u63A5\u8D1F\u8D23\u7684\u4E3B\u7BA1\u4EBA\u5458\u548C\u5176\u4ED6\u76F4\u63A5\u8D23\u4EFB\u4EBA\u5458\uFF0C\u4F9D\u6CD5\u7ED9\u4E88\u884C\u653F\u5904\u5206\u3002\u201D<br> \u2003\u2003\u901A\u8FC7\u4EE5\u4E0A\u6CD5\u5F8B\u4FEE\u8BA2\uFF0C\u65B0\u73AF\u5883\u4FDD\u62A4\u6CD5\u548C\u65B0\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u53D6\u6D88\u4E86\u201C\u9650\u671F\u8865\u529E\u624B\u7EED\u201D\u7684\u8981\u6C42\u3002</p><h3 id="\u4E8C-\u6CD5\u5F8B\u9002\u7528" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u6CD5\u5F8B\u9002\u7528" aria-hidden="true">#</a> \uFF08\u4E8C\uFF09\u6CD5\u5F8B\u9002\u7528</h3><p>\u2003\u2003\u5173\u4E8E\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A\uFF0C\u6211\u90E82016\u5E741\u67088\u65E5\u4F5C\u51FA\u7684\u300A\u5173\u4E8E\u3008\u73AF\u5883\u4FDD\u62A4\u6CD5\u3009\uFF082014\u4FEE\u8BA2\uFF09\u7B2C\u516D\u5341\u4E00\u6761\u9002\u7528\u6709\u5173\u95EE\u9898\u7684\u590D\u51FD\u300B\uFF08\u73AF\u653F\u6CD5\u51FD\u30142016\u30156\u53F7\uFF09\u5DF2\u5BF9\u201C\u65B0\u6CD5\u5B9E\u65BD\u524D\u5DF2\u7ECF\u64C5\u81EA\u5F00\u5DE5\u5EFA\u8BBE\u7684\u9879\u76EE\u7684\u6CD5\u5F8B\u9002\u7528\u201D\u4F5C\u51FA\u76F8\u5173\u89E3\u91CA\uFF0C\u73B0\u9488\u5BF9\u5B9E\u8DF5\u4E2D\u9047\u5230\u7684\u95EE\u9898\uFF0C\u8FDB\u4E00\u6B65\u63D0\u51FA\u8865\u5145\u610F\u89C1\u5982\u4E0B\uFF1A<br> \u2003\u20031.\u5EFA\u8BBE\u9879\u76EE\u4E8E2015\u5E741\u67081\u65E5\u540E\u5F00\u5DE5\u5EFA\u8BBE\uFF0C\u6216\u80052015\u5E741\u67081\u65E5\u4E4B\u524D\u5DF2\u7ECF\u5F00\u5DE5\u5EFA\u8BBE\u4E14\u4E4B\u540E\u4ECD\u7136\u8FDB\u884C\u5EFA\u8BBE\u7684\uFF0C\u7ACB\u6848\u67E5\u5904\u7684\u73AF\u4FDD\u90E8\u95E8\u5E94\u5F53\u9002\u7528\u65B0\u73AF\u5883\u4FDD\u62A4\u6CD5\u7B2C\u516D\u5341\u4E00\u6761\u7684\u89C4\u5B9A\u8FDB\u884C\u5904\u7F5A\uFF0C\u4E0D\u518D\u4F9D\u636E\u4FEE\u6B63\u524D\u7684\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u4F5C\u51FA\u201C\u9650\u671F\u8865\u529E\u624B\u7EED\u201D\u7684\u884C\u653F\u547D\u4EE4\u3002<br> \u2003\u20032.\u5EFA\u8BBE\u9879\u76EE\u4E8E2016\u5E749\u67081\u65E5\u540E\u5F00\u5DE5\u5EFA\u8BBE\uFF0C\u6216\u80052016\u5E749\u67081\u65E5\u4E4B\u524D\u5DF2\u7ECF\u5F00\u5DE5\u5EFA\u8BBE\u4E14\u4E4B\u540E\u4ECD\u7136\u8FDB\u884C\u5EFA\u8BBE\u7684\uFF0C\u7ACB\u6848\u67E5\u5904\u7684\u73AF\u4FDD\u90E8\u95E8\u5E94\u5F53\u9002\u7528\u65B0\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u7B2C\u4E09\u5341\u4E00\u6761\u7684\u89C4\u5B9A\u8FDB\u884C\u5904\u7F5A\uFF0C\u4E0D\u518D\u4F9D\u636E\u4FEE\u6B63\u524D\u7684\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u4F5C\u51FA\u201C\u9650\u671F\u8865\u529E\u624B\u7EED\u201D\u7684\u884C\u653F\u547D\u4EE4\u3002</p><h2 id="\u4E8C\u3001\u5173\u4E8E-\u672A\u6279\u5148\u5EFA-\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A\u8FFD\u6EAF\u671F\u9650" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u5173\u4E8E-\u672A\u6279\u5148\u5EFA-\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A\u8FFD\u6EAF\u671F\u9650" aria-hidden="true">#</a> \u4E8C\u3001\u5173\u4E8E\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A\u8FFD\u6EAF\u671F\u9650</h2><h3 id="\u4E00-\u76F8\u5173\u6CD5\u5F8B\u89C4\u5B9A-1" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u76F8\u5173\u6CD5\u5F8B\u89C4\u5B9A-1" aria-hidden="true">#</a> \uFF08\u4E00\uFF09\u76F8\u5173\u6CD5\u5F8B\u89C4\u5B9A</h3><p>\u2003\u2003\u884C\u653F\u5904\u7F5A\u6CD5\u7B2C\u4E8C\u5341\u4E5D\u6761\u89C4\u5B9A\uFF1A\u201C\u8FDD\u6CD5\u884C\u4E3A\u5728\u4E8C\u5E74\u5185\u672A\u88AB\u53D1\u73B0\u7684\uFF0C\u4E0D\u518D\u7ED9\u4E88\u884C\u653F\u5904\u7F5A\u3002\u6CD5\u5F8B\u53E6\u6709\u89C4\u5B9A\u7684\u9664\u5916\u3002\u524D\u6B3E\u89C4\u5B9A\u7684\u671F\u9650\uFF0C\u4ECE\u8FDD\u6CD5\u884C\u4E3A\u53D1\u751F\u4E4B\u65E5\u8D77\u8BA1\u7B97\uFF1B\u8FDD\u6CD5\u884C\u4E3A\u6709\u8FDE\u7EED\u6216\u8005\u7EE7\u7EED\u72B6\u6001\u7684\uFF0C\u4ECE\u884C\u4E3A\u7EC8\u4E86\u4E4B\u65E5\u8D77\u8BA1\u7B97\u3002\u201D</p><h3 id="\u4E8C-\u8FFD\u6EAF\u671F\u9650\u7684\u8D77\u7B97\u65F6\u95F4" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u8FFD\u6EAF\u671F\u9650\u7684\u8D77\u7B97\u65F6\u95F4" aria-hidden="true">#</a> \uFF08\u4E8C\uFF09\u8FFD\u6EAF\u671F\u9650\u7684\u8D77\u7B97\u65F6\u95F4</h3><p>\u2003\u2003\u6839\u636E\u4E0A\u8FF0\u6CD5\u5F8B\u89C4\u5B9A\uFF0C\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A\u8FFD\u6EAF\u671F\u9650\u5E94\u5F53\u81EA\u5EFA\u8BBE\u884C\u4E3A\u7EC8\u4E86\u4E4B\u65E5\u8D77\u8BA1\u7B97\u3002\u56E0\u6B64\uFF0C\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u81EA\u5EFA\u8BBE\u884C\u4E3A\u7EC8\u4E86\u4E4B\u65E5\u8D77\u4E8C\u5E74\u5185\u672A\u88AB\u53D1\u73B0\u7684\uFF0C\u73AF\u4FDD\u90E8\u95E8\u5E94\u5F53\u9075\u5B88\u884C\u653F\u5904\u7F5A\u6CD5\u7B2C\u4E8C\u5341\u4E5D\u6761\u7684\u89C4\u5B9A\uFF0C\u4E0D\u4E88\u884C\u653F\u5904\u7F5A\u3002</p><h3 id="\u4E09-\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD-\u4E09\u540C\u65F6-\u9A8C\u6536\u5236\u5EA6\u7684\u884C\u653F\u5904\u7F5A" tabindex="-1"><a class="header-anchor" href="#\u4E09-\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD-\u4E09\u540C\u65F6-\u9A8C\u6536\u5236\u5EA6\u7684\u884C\u653F\u5904\u7F5A" aria-hidden="true">#</a> \uFF08\u4E09\uFF09\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u7684\u884C\u653F\u5904\u7F5A</h3><p>\u2003\u20031.\u5EFA\u8BBE\u5355\u4F4D\u540C\u65F6\u6784\u6210\u201C\u672A\u6279\u5148\u5EFA\u201D\u548C\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u4E24\u4E2A\u8FDD\u6CD5\u884C\u4E3A\u7684\uFF0C\u5E94\u5F53\u5206\u522B\u4F9D\u6CD5\u4F5C\u51FA\u76F8\u5E94\u5904\u7F5A\u3002<br> \u2003\u2003\u5BF9\u5EFA\u8BBE\u9879\u76EE\u201C\u672A\u6279\u5148\u5EFA\u201D\u5E76\u5DF2\u5EFA\u6210\u6295\u5165\u751F\u4EA7\u6216\u8005\u4F7F\u7528\uFF0C\u540C\u65F6\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u7684\u8FDD\u6CD5\u884C\u4E3A\u5E94\u5F53\u5982\u4F55\u5904\u7F5A\uFF0C\u5168\u56FD\u4EBA\u5927\u5E38\u59D4\u4F1A\u6CD5\u5236\u5DE5\u4F5C\u59D4\u5458\u4F1A2007\u5E743\u670821\u65E5\u4F5C\u51FA\u7684\u300A\u5173\u4E8E\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u7BA1\u7406\u6709\u5173\u6CD5\u5F8B\u9002\u7528\u95EE\u9898\u7684\u7B54\u590D\u610F\u89C1\u300B\uFF08\u6CD5\u5DE5\u59D4\u590D\u30142007\u30152\u53F7\uFF09\u89C4\u5B9A\uFF1A\u201C\u5173\u4E8E\u5EFA\u8BBE\u5355\u4F4D\u672A\u4F9D\u6CD5\u62A5\u6279\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u5374\u5DF2\u5EFA\u6210\u5EFA\u8BBE\u9879\u76EE\uFF0C\u540C\u65F6\u8BE5\u5EFA\u8BBE\u9879\u76EE\u9700\u8981\u914D\u5957\u5EFA\u8BBE\u7684\u73AF\u5883\u4FDD\u62A4\u8BBE\u65BD\u672A\u5EFA\u6210\u3001\u672A\u7ECF\u9A8C\u6536\u6216\u8005\u7ECF\u9A8C\u6536\u4E0D\u5408\u683C\uFF0C\u4E3B\u4F53\u5DE5\u7A0B\u6B63\u5F0F\u6295\u5165\u751F\u4EA7\u6216\u8005\u4F7F\u7528\u7684\uFF0C\u5E94\u5F53\u5206\u522B\u4F9D\u7167\u300A\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u300B\u7B2C\u4E09\u5341\u4E00\u6761\u3001\u300A\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u4FDD\u62A4\u7BA1\u7406\u6761\u4F8B\u300B\u7B2C\u4E8C\u5341\u516B\u6761\u7684\u89C4\u5B9A\u4F5C\u51FA\u76F8\u5E94\u5904\u7F5A\u3002\u201D<br> \u2003\u2003\u636E\u6B64\uFF0C\u5EFA\u8BBE\u5355\u4F4D\u540C\u65F6\u6784\u6210\u201C\u672A\u6279\u5148\u5EFA\u201D\u548C\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u4E24\u4E2A\u8FDD\u6CD5\u884C\u4E3A\u7684\uFF0C\u5E94\u5F53\u5206\u522B\u4F9D\u6CD5\u4F5C\u51FA\u76F8\u5E94\u5904\u7F5A\u3002<br> \u2003\u20032.\u5BF9\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u7684\u5904\u7F5A\uFF0C\u4E0D\u53D7\u201C\u672A\u6279\u5148\u5EFA\u201D\u884C\u653F\u5904\u7F5A\u8FFD\u6EAF\u671F\u9650\u7684\u5F71\u54CD<br> \u2003\u2003\u5EFA\u8BBE\u9879\u76EE\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u6295\u5165\u751F\u4EA7\u6216\u8005\u4F7F\u7528\u671F\u95F4\uFF0C\u7531\u4E8E\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u7684\u8FDD\u6CD5\u884C\u4E3A\u4E00\u76F4\u5904\u4E8E\u8FDE\u7EED\u6216\u8005\u7EE7\u7EED\u72B6\u6001\uFF0C\u56E0\u6B64\uFF0C\u5373\u4F7F\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u5DF2\u8D85\u8FC7\u4E8C\u5E74\u884C\u653F\u5904\u7F5A\u8FFD\u6EAF\u671F\u9650\uFF0C\u73AF\u4FDD\u90E8\u95E8\u4ECD\u53EF\u4EE5\u5BF9\u8FDD\u53CD\u73AF\u4FDD\u8BBE\u65BD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u7684\u8FDD\u6CD5\u884C\u4E3A\u4F9D\u6CD5\u4F5C\u51FA\u5904\u7F5A\uFF0C\u4E0D\u53D7\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u8FFD\u6EAF\u671F\u9650\u7684\u5F71\u54CD\u3002</p><h3 id="\u56DB-\u5176\u4ED6\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A" tabindex="-1"><a class="header-anchor" href="#\u56DB-\u5176\u4ED6\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A" aria-hidden="true">#</a> \uFF08\u56DB\uFF09\u5176\u4ED6\u8FDD\u6CD5\u884C\u4E3A\u7684\u884C\u653F\u5904\u7F5A</h3><p>\u2003\u2003\u5EFA\u8BBE\u9879\u76EE\u201C\u672A\u6279\u5148\u5EFA\u201D\u5E76\u6295\u5165\u751F\u4EA7\u6216\u8005\u4F7F\u7528\u540E\uFF0C\u6709\u5173\u5355\u4F4D\u6216\u8005\u4E2A\u4EBA\u5177\u6709\u8D85\u8FC7\u6C61\u67D3\u7269\u6392\u653E\u6807\u51C6\u6392\u6C61\uFF0C\u901A\u8FC7\u6697\u7BA1\u3001\u6E17\u4E95\u3001\u6E17\u5751\u3001\u704C\u6CE8\u6216\u8005\u7BE1\u6539\u3001\u4F2A\u9020\u76D1\u6D4B\u6570\u636E\uFF0C\u6216\u8005\u4E0D\u6B63\u5E38\u8FD0\u884C\u6C61\u67D3\u9632\u6CBB\u8BBE\u65BD\u7B49\u9003\u907F\u76D1\u7BA1\u7684\u65B9\u5F0F\u6392\u6C61\u7B49\u60C5\u5F62\u4E4B\u4E00\uFF0C\u5206\u522B\u6784\u6210\u72EC\u7ACB\u8FDD\u6CD5\u884C\u4E3A\u7684\uFF0C\u73AF\u4FDD\u90E8\u95E8\u5E94\u5F53\u5BF9\u76F8\u5173\u8FDD\u6CD5\u884C\u4E3A\u4F9D\u6CD5\u4E88\u4EE5\u5904\u7F5A\u3002</p><h2 id="\u4E09\u3001\u5173\u4E8E\u5EFA\u8BBE\u5355\u4F4D\u53EF\u5426\u4E3B\u52A8\u8865\u4EA4\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\u62A5\u9001\u5BA1\u6279" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u5173\u4E8E\u5EFA\u8BBE\u5355\u4F4D\u53EF\u5426\u4E3B\u52A8\u8865\u4EA4\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\u62A5\u9001\u5BA1\u6279" aria-hidden="true">#</a> \u4E09\u3001\u5173\u4E8E\u5EFA\u8BBE\u5355\u4F4D\u53EF\u5426\u4E3B\u52A8\u8865\u4EA4\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\u62A5\u9001\u5BA1\u6279</h2><p>\u2003\u2003<b>\uFF08\u4E00\uFF09\u65B0\u73AF\u5883\u4FDD\u62A4\u6CD5\u548C\u65B0\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u5E76\u672A\u7981\u6B62\u5EFA\u8BBE\u5355\u4F4D\u4E3B\u52A8\u8865\u4EA4\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\u62A5\u9001\u5BA1\u6279</b><br> \u2003\u2003\u5BF9\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\uFF0C2014\u5E74\u4FEE\u8BA2\u7684\u65B0\u73AF\u5883\u4FDD\u62A4\u6CD5\u7B2C\u516D\u5341\u4E00\u6761\u589E\u52A0\u4E86\u5904\u7F5A\u6761\u6B3E\uFF0C\u8BE5\u6761\u6B3E\u4E0E\u539F\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\uFF082002\u5E74\uFF09\u7B2C\u4E09\u5341\u4E00\u6761\u76F8\u6BD4\uFF0C\u672A\u89C4\u5B9A\u201C\u8D23\u4EE4\u9650\u671F\u8865\u529E\u624B\u7EED\u201D\u7684\u5185\u5BB9\uFF1B2016\u5E74\u4FEE\u6B63\u7684\u65B0\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u7B2C\u4E09\u5341\u4E00\u6761\uFF0C\u4EA6\u5220\u9664\u4E86\u539F\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u201C\u9650\u671F\u8865\u529E\u624B\u7EED\u201D\u7684\u89C4\u5B9A\u3002\u4E0D\u518D\u5C06\u201C\u9650\u671F\u8865\u529E\u624B\u7EED\u201D\u4F5C\u4E3A\u884C\u653F\u5904\u7F5A\u7684\u524D\u7F6E\u6761\u4EF6\uFF0C\u4F46\u5E76\u672A\u7981\u6B62\u5EFA\u8BBE\u5355\u4F4D\u4E3B\u52A8\u8865\u4EA4\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\u62A5\u9001\u5BA1\u6279\u3002</p><p>\u2003\u2003<b>\uFF08\u4E8C\uFF09\u5EFA\u8BBE\u5355\u4F4D\u4E3B\u52A8\u8865\u4EA4\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\u5E76\u62A5\u9001\u73AF\u4FDD\u90E8\u95E8\u5BA1\u67E5\u7684\uFF0C\u6709\u6743\u5BA1\u6279\u7684\u73AF\u4FDD\u90E8\u95E8\u5E94\u5F53\u53D7\u7406</b><br> \u2003\u2003\u56E0\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u53D7\u5230\u73AF\u4FDD\u90E8\u95E8\u4F9D\u636E\u65B0\u73AF\u5883\u4FDD\u62A4\u6CD5\u548C\u65B0\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u4F5C\u51FA\u7684\u5904\u7F5A\uFF0C\u6216\u8005\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u81EA\u5EFA\u8BBE\u884C\u4E3A\u7EC8\u4E86\u4E4B\u65E5\u8D77\u4E8C\u5E74\u5185\u672A\u88AB\u53D1\u73B0\u800C\u672A\u4E88\u884C\u653F\u5904\u7F5A\u7684\uFF0C\u5EFA\u8BBE\u5355\u4F4D\u4E3B\u52A8\u8865\u4EA4\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u4E66\u3001\u62A5\u544A\u8868\u5E76\u62A5\u9001\u73AF\u4FDD\u90E8\u95E8\u5BA1\u67E5\u7684\uFF0C\u6709\u6743\u5BA1\u6279\u7684\u73AF\u4FDD\u90E8\u95E8\u5E94\u5F53\u53D7\u7406\uFF0C\u5E76\u6839\u636E\u4E0D\u540C\u60C5\u5F62\u5206\u522B\u4F5C\u51FA\u76F8\u5E94\u5904\u7406\uFF1A<br> \u2003\u20031.\u5BF9\u7B26\u5408\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u5BA1\u6279\u8981\u6C42\u7684\uFF0C\u4F9D\u6CD5\u4F5C\u51FA\u6279\u51C6\u51B3\u5B9A\u3002<br> \u2003\u20032.\u5BF9\u4E0D\u7B26\u5408\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u5BA1\u6279\u8981\u6C42\u7684\uFF0C\u4F9D\u6CD5\u4E0D\u4E88\u6279\u51C6\uFF0C\u5E76\u53EF\u4EE5\u4F9D\u6CD5\u8D23\u4EE4\u6062\u590D\u539F\u72B6\u3002<br> \u2003\u2003\u5EFA\u8BBE\u5355\u4F4D\u540C\u65F6\u5B58\u5728\u8FDD\u53CD\u201C\u4E09\u540C\u65F6\u201D\u9A8C\u6536\u5236\u5EA6\u3001\u8D85\u8FC7\u6C61\u67D3\u7269\u6392\u653E\u6807\u51C6\u6392\u6C61\u7B49\u8FDD\u6CD5\u884C\u4E3A\u7684\uFF0C\u5E94\u5F53\u4F9D\u6CD5\u4E88\u4EE5\u5904\u7F5A\u3002</p><p>\u2003\u2003\u6211\u90E8\u4E4B\u524D\u5370\u53D1\u7684\u76F8\u5173\u89E3\u91CA\u4E0E\u672C\u610F\u89C1\u4E0D\u4E00\u81F4\u7684\uFF0C\u4EE5\u672C\u610F\u89C1\u4E3A\u51C6\u3002\u539F\u56FD\u5BB6\u73AF\u5883\u4FDD\u62A4\u603B\u5C40\u300A\u5173\u4E8E\u5982\u4F55\u8BA4\u5B9A\u5EFA\u8BBE\u5355\u4F4D\u8FDD\u6CD5\u884C\u4E3A\u8FDE\u7EED\u6027\u95EE\u9898\u7684\u590D\u51FD\u300B\uFF08\u73AF\u53D1\u30141999\u301523\u53F7\uFF09\u548C\u300A\u5173\u4E8E\u3008\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6CD5\u3009\u7B2C\u4E09\u5341\u4E00\u6761\u6CD5\u5F8B\u9002\u7528\u95EE\u9898\u7684\u590D\u51FD\u300B\uFF08\u73AF\u51FD\u30142004\u3015470\u53F7\uFF09\u540C\u65F6\u5E9F\u6B62\u3002</p><div style="text-align:right;"><p>\u73AF\u5883\u4FDD\u62A4\u90E8</p><p>2018\u5E742\u670822\u65E5</p></div><hr>',21),p={class:"custom-container warning"},b=e("p",{class:"custom-container-title"},"\u539F\u6587\u94FE\u63A5",-1),l={href:"http://www.mee.gov.cn/gkml/hbb/bh/201802/t20180228_431889.htm",target:"_blank",rel:"noopener noreferrer"},_=i("http://www.mee.gov.cn/gkml/hbb/bh/201802/t20180228_431889.htm");function f(x,m){const a=s("ExternalLinkIcon");return t(),h("div",null,[o,e("div",p,[b,e("p",null,[e("a",l,[_,n(a)])])])])}const g=r(c,[["render",f],["__file","gyjsxmwpxjwfxwflsywtdyj.html.vue"]]);export{g as default};
