import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as s,c as o,a as t,b as e,e as n,d as b,r as c}from"./app.dffca043.js";const p={},i=n('<details class="custom-container details"><summary>\u76EE\u5F55</summary><ul><li><p>\u7B2C1~18\u6761\uFF1A</p><p><a href="#t1">1</a>\u2003<a href="#t2">2</a>\u2003<a href="#t3">3</a>\u2003<a href="#t4">4</a>\u2003<a href="#t5">5</a>\u2003<a href="#t6">6</a>\u2003<a href="#t7">7</a>\u2003<a href="#t8">8</a>\u2003<a href="#t9">9</a>\u2003<a href="#t10">10</a>\u2003<a href="#t11">11</a>\u2003<a href="#t12">12</a>\u2003<a href="#t13">13</a>\u2003<a href="#t14">14</a>\u2003<a href="#t15">15</a>\u2003<a href="#t16">16</a>\u2003<a href="#t17">17</a>\u2003<a href="#t18">18</a></p></li></ul></details><p id="t1">\u2003\u2003<b>\u7B2C\u4E00\u6761</b>\u2003\u4E3A\u5168\u9762\u63A8\u8FDB\u4F9D\u6CD5\u884C\u653F\uFF0C\u8FDB\u4E00\u6B65\u89C4\u8303\u884C\u653F\u6267\u6CD5\u884C\u4E3A\uFF0C\u786E\u4FDD\u884C\u653F\u5904\u7F5A\u7684\u5408\u6CD5\u6027\u3001\u5408\u7406\u6027\uFF0C\u6839\u636E\u300A\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u884C\u653F\u5904\u7F5A\u6CD5\u300B\u300A\u73AF\u5883\u884C\u653F\u5904\u7F5A\u529E\u6CD5\u300B\u300A\u5173\u4E8E\u8FDB\u4E00\u6B65\u89C4\u8303\u9002\u7528\u73AF\u5883\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u7684\u6307\u5BFC\u610F\u89C1\u300B\uFF08\u73AF\u6267\u6CD5\u30142019\u301542\u53F7\uFF09\u7B49\u89C4\u5B9A\uFF0C\u7ED3\u5408\u6211\u5E02\u751F\u6001\u73AF\u5883\u884C\u653F\u6267\u6CD5\u5DE5\u4F5C\u5B9E\u9645\uFF0C\u7279\u5236\u5B9A\u672C\u89C4\u5B9A\u3002</p><p id="t2">\u2003\u2003<b>\u7B2C\u4E8C\u6761</b>\u2003\u672C\u89C4\u5B9A\u6240\u79F0\u751F\u6001\u73AF\u5883\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\uFF0C\u662F\u6307\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u5728\u6CD5\u5F8B\u3001\u6CD5\u89C4\u3001\u89C4\u7AE0\u89C4\u5B9A\u7684\u884C\u653F\u5904\u7F5A\u6743\u9650\u8303\u56F4\u5185\uFF0C\u5BF9\u516C\u6C11\u3001\u6CD5\u4EBA\u6216\u8005\u5176\u4ED6\u7EC4\u7EC7\uFF08\u4EE5\u4E0B\u79F0\u201C\u5F53\u4E8B\u4EBA\u201D\uFF09\u8FDD\u53CD\u884C\u653F\u7BA1\u7406\u79E9\u5E8F\u7684\u884C\u4E3A\u51B3\u5B9A\u662F\u5426\u7ED9\u4E88\u884C\u653F\u5904\u7F5A\u3001\u7ED9\u4E88\u4F55\u79CD\u884C\u653F\u5904\u7F5A\u548C\u7ED9\u4E88\u4F55\u79CD\u5E45\u5EA6\u884C\u653F\u5904\u7F5A\u7684\u6743\u9650\u3002</p><p id="t3">\u2003\u2003<b>\u7B2C\u4E09\u6761</b>\u2003\u672C\u89C4\u5B9A\u9002\u7528\u4E8E\u672C\u5E02\u5404\u7EA7\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u5728\u7ACB\u6848\u540E\uFF0C\u5B9E\u65BD\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u7684\u89C4\u8303\u548C\u76D1\u7763\u3002\u672C\u89C4\u5B9A\u53CA\u9644\u4EF6\u4E0D\u4F5C\u4E3A\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u5B9E\u65BD\u884C\u653F\u5904\u7F5A\u7684\u6CD5\u5F8B\u4F9D\u636E\uFF0C\u5728\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u5185\u90E8\u6CD5\u5F8B\u6587\u4E66\u8BF4\u660E\u7406\u7531\u65F6\u4E88\u4EE5\u63F4\u5F15\u3002</p><p id="t4">\u2003\u2003<b>\u7B2C\u56DB\u6761</b>\u2003\u5728\u751F\u6001\u73AF\u5883\u6267\u6CD5\u8FC7\u7A0B\u4E2D\uFF0C\u5BF9\u5177\u4F53\u8FDD\u6CD5\u884C\u4E3A\u51B3\u5B9A\u662F\u5426\u7ED9\u4E88\u884C\u653F\u5904\u7F5A\u3001\u786E\u5B9A\u5904\u7F5A\u79CD\u7C7B\u3001\u88C1\u5B9A\u5904\u7F5A\u5E45\u5EA6\u65F6\uFF0C\u5E94\u5F53\u4E25\u683C\u9075\u5B88\u4EE5\u4E0B\u539F\u5219\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u8FC7\u7F5A\u76F8\u5F53\u7684\u539F\u5219\u3002\u5B9E\u65BD\u884C\u653F\u5904\u7F5A\u5FC5\u987B\u4EE5\u4E8B\u5B9E\u4E3A\u4F9D\u636E\uFF0C\u4E0E\u8FDD\u6CD5\u884C\u4E3A\u7684\u4E8B\u5B9E\u3001\u6027\u8D28\u3001\u60C5\u8282\u4EE5\u53CA\u793E\u4F1A\u5371\u5BB3\u7A0B\u5EA6\u76F8\u5F53\u3002<br> \u2003\u2003\uFF08\u4E8C\uFF09\u7F5A\u6559\u7ED3\u5408\u7684\u539F\u5219\u3002\u5B9E\u65BD\u884C\u653F\u5904\u7F5A\uFF0C\u7EA0\u6B63\u8FDD\u6CD5\u884C\u4E3A\uFF0C\u5E94\u5F53\u575A\u6301\u5904\u7F5A\u4E0E\u6559\u80B2\u76F8\u7ED3\u5408\uFF0C\u6559\u80B2\u516C\u6C11\u3001\u6CD5\u4EBA\u6216\u8005\u5176\u4ED6\u7EC4\u7EC7\u81EA\u89C9\u5B88\u6CD5\u3002<br> \u2003\u2003\uFF08\u4E09\uFF09\u7EFC\u5408\u5206\u6790\u7684\u539F\u5219\u3002\u5BF9\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u5B9E\u65BD\u5904\u7F5A\u65F6\uFF0C\u5E94\u5F53\u5728\u6CD5\u5B9A\u7684\u5904\u7F5A\u79CD\u7C7B\u548C\u5E45\u5EA6\u8303\u56F4\u5185\uFF0C\u7EFC\u5408\u8003\u8651\u4EE5\u4E0B\u60C5\u8282\uFF1A<br> \u2003\u20031\u3001\u5F53\u4E8B\u4EBA\u7684\u4E3B\u89C2\u8FC7\u9519\u7A0B\u5EA6\uFF1B<br> \u2003\u20032\u3001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u7684\u5177\u4F53\u65B9\u6CD5\u6216\u8005\u624B\u6BB5\uFF0C\u5371\u5BB3\u7684\u5177\u4F53\u5BF9\u8C61\uFF1B<br> \u2003\u20033\u3001\u8FDD\u6CD5\u884C\u4E3A\u9020\u6210\u7684\u5371\u5BB3\u540E\u679C\uFF1A\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u9020\u6210\u7684\u73AF\u5883\u6C61\u67D3\u3001\u751F\u6001\u7834\u574F\u7A0B\u5EA6\u4EE5\u53CA\u793E\u4F1A\u5F71\u54CD\uFF1B<br> \u2003\u20034\u3001\u5F53\u4E8B\u4EBA\u6539\u6B63\u8FDD\u6CD5\u884C\u4E3A\u7684\u6001\u5EA6\u548C\u6240\u91C7\u53D6\u7684\u6539\u6B63\u63AA\u65BD\u53CA\u5176\u6548\u679C\uFF1B<br> \u2003\u20035\u3001\u5F53\u4E8B\u4EBA\u7684\u8FDD\u6CD5\u884C\u4E3A\u662F\u521D\u72AF\u8FD8\u662F\u518D\u72AF\u3002<br> \u2003\u2003\uFF08\u56DB\uFF09\u516C\u5E73\u516C\u6B63\u7684\u539F\u5219\u3002\u5728\u8FDD\u6CD5\u884C\u4E3A\u7684\u4E8B\u5B9E\u3001\u6027\u8D28\u3001\u60C5\u8282\u53CA\u793E\u4F1A\u5371\u5BB3\u7A0B\u5EA6\u7B49\u56E0\u7D20\u76F8\u540C\u6216\u76F8\u4F3C\u7684\u60C5\u51B5\u4E0B\uFF0C\u53C2\u7167\u540C\u7C7B\u578B\u6848\u4EF6\u5904\u7406\u7ED3\u679C\uFF0C\u9002\u7528\u7684\u6CD5\u5F8B\u4F9D\u636E\u5E94\u5F53\u76F8\u540C\uFF0C\u5904\u7F5A\u79CD\u7C7B\u3001\u5904\u7F5A\u5E45\u5EA6\u5E94\u5F53\u57FA\u672C\u76F8\u5F53\u3002</p><p id="t5">\u2003\u2003<b>\u7B2C\u4E94\u6761</b>\u2003\u4ECE\u91CD\u5904\u7F5A\u9002\u7528\u60C5\u5F62\u3002\u6709\u4EE5\u4E0B\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u4E14\u300A\u6C5F\u95E8\u5E02\u751F\u6001\u73AF\u5883\u5C40\u5173\u4E8E\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u88C1\u91CF\u6807\u51C6\u53C2\u7167\u8868\u300B\u672A\u505A\u89C4\u5B9A\u7684\uFF0C\u6309\u8FDD\u6CD5\u7A0B\u5EA6\u4EE5\u8BE5\u6863\u6B21\u6700\u9AD8\u9650\u5EA6\u5904\u7F5A\uFF0C\u4F46\u4E0D\u5F97\u9AD8\u4E8E\u6700\u9AD8\u5904\u7F5A\u989D\u5EA6\uFF1A<br> \u2003\u20031\u3001\u5728\u6848\u4EF6\u67E5\u5904\u4E2D\u5BF9\u6267\u6CD5\u4EBA\u5458\u8FDB\u884C\u5A01\u80C1\u3001\u8FB1\u9A82\u3001\u6BB4\u6253\u3001\u6050\u5413\u3001\u6253\u51FB\u62A5\u590D\u6216\u8005\u5176\u4ED6\u66B4\u529B\u6297\u62D2\u6267\u6CD5\u884C\u4E3A\u7684\uFF1B<br> \u2003\u20032\u3001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u9020\u6210\u8DE8\u53BF\uFF08\u533A\uFF09\u7EA7\u884C\u653F\u533A\u57DF\u73AF\u5883\u6C61\u67D3\u7684\uFF1B<br> \u2003\u20033\u3001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u5F15\u8D77\u4E0D\u826F\u793E\u4F1A\u53CD\u54CD\u7684\uFF1B<br> \u2003\u20034\u3001\u8FDD\u6CD5\u5411\u73AF\u5883\u6392\u653E\u6216\u503E\u5012\u5371\u9669\u5E9F\u7269\u3001\u542B\u91CD\u91D1\u5C5E\u7684\u6C61\u67D3\u7269\u7B49\u6709\u6BD2\u6709\u5BB3\u7269\u8D28\u7684\uFF1B<br> \u2003\u20035\u3001\u6CD5\u5F8B\u6CD5\u89C4\u89C4\u7AE0\u89C4\u5B9A\u7684\u5E94\u5F53\u4ECE\u91CD\u5904\u7F5A\u7684\u5176\u4ED6\u60C5\u5F62\uFF1B<br> \u2003\u2003\u5F53\u4E8B\u4EBA\u5728\u4E24\u5E74\u5185\u56E0\u540C\u7C7B\u8FDD\u6CD5\u884C\u4E3A\u88AB\u5904\u7F5A2\u6B21\u7684\uFF0C\u4EE5\u8BE5\u6863\u6B21\u6700\u9AD8\u9650\u5EA6\u5904\u7F5A\uFF1B\u88AB\u5904\u7F5A3\u6B21\u4EE5\u4E0A\u7684\uFF0C\u4E0A\u8C03\u4E00\u4E2A\u6863\u6B21\u5904\u7F5A\uFF0C\u4F46\u4E0D\u5F97\u9AD8\u4E8E\u6700\u9AD8\u5904\u7F5A\u989D\u5EA6\u3002<br> \u2003\u2003\u6709\u4EE5\u4E0B\u60C5\u5F62\u7684\uFF0C\u4E14\u300A\u6C5F\u95E8\u5E02\u751F\u6001\u73AF\u5883\u5C40\u5173\u4E8E\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u88C1\u91CF\u6807\u51C6\u53C2\u7167\u8868\u300B\u672A\u505A\u89C4\u5B9A\u7684\uFF0C\u53EF\u4E0A\u8C03\u4E00\u4E2A\u6863\u6B21\u5904\u7F5A\uFF1B\u5DF2\u7ECF\u662F\u6700\u9AD8\u6863\u6B21\u7684\uFF0C\u6309\u6700\u9AD8\u5904\u7F5A\u989D\u5EA6\u5904\u7F5A\uFF0C\u4F46\u4E0D\u5F97\u9AD8\u4E8E\u6700\u9AD8\u5904\u7F5A\u989D\u5EA6\uFF1A<br> \u2003\u20031\u3001\u9020\u6210\u8F83\u5927\u6216\u4EE5\u4E0A\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u6216\u73AF\u5883\u6C61\u67D3\u4E8B\u6545\u7684\uFF1B<br> \u2003\u20032\u3001\u5F15\u8D77\u8F83\u5927\u6216\u4EE5\u4E0A\u7FA4\u4F53\u6027\u4E8B\u4EF6\u7684\uFF1B<br> \u2003\u20033\u3001\u81F4\u4F7F\u4E61\u9547\u4EE5\u4E0A\u96C6\u4E2D\u5F0F\u996E\u7528\u6C34\u6C34\u6E90\u53D6\u6C34\u4E2D\u65AD\u7684\uFF1B<br> \u2003\u20034\u3001\u91C7\u53D6\u66B4\u529B\u624B\u6BB5\u6297\u62D2\u6267\u6CD5\uFF0C\u4E14\u9020\u6210\u4EBA\u5458\u4F24\u4EA1\u6216\u8BBE\u5907\u635F\u6BC1\u7684\uFF1B<br> \u2003\u20035\u3001\u6CD5\u5F8B\u6CD5\u89C4\u89C4\u7AE0\u89C4\u5B9A\u7684\u5E94\u5F53\u5B9E\u65BD\u6700\u9AD8\u9650\u5EA6\u5904\u7F5A\u7684\u5176\u4ED6\u60C5\u5F62\u3002</p><p id="t6">\u2003\u2003<b>\u7B2C\u516D\u6761</b>\u2003\u4ECE\u8F7B\u5904\u7F5A\u9002\u7528\u60C5\u5F62\u3002\u6709\u4EE5\u4E0B\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u4E14\u300A\u6C5F\u95E8\u5E02\u751F\u6001\u73AF\u5883\u5C40\u5173\u4E8E\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u88C1\u91CF\u6807\u51C6\u53C2\u7167\u8868\u300B\u672A\u505A\u89C4\u5B9A\u7684\uFF0C\u53EF\u6309\u6240\u5728\u88C1\u91CF\u6863\u6B21\u7684\u8F83\u4F4E\u9650\u5904\u7F5A\uFF1A<br> \u2003\u20031\u3001\u4E3B\u52A8\u6D88\u9664\u6216\u8005\u51CF\u8F7B\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u5371\u5BB3\u540E\u679C\u7684\uFF1B<br> \u2003\u20032\u3001\u914D\u5408\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u67E5\u5904\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u6709\u7ACB\u529F\u8868\u73B0\u7684\uFF1B<br> \u2003\u20033\u3001\u5C5E\u521D\u72AF\uFF0C\u4E14\u4E0D\u5B58\u5728\u8FDD\u6CD5\u7684\u4E3B\u89C2\u6545\u610F\uFF0C\u9020\u6210\u7684\u73AF\u5883\u6C61\u67D3\u8F7B\u5FAE\u3001\u751F\u6001\u7834\u574F\u7A0B\u5EA6\u8F83\u5C0F\u6216\u8005\u5C1A\u672A\u4EA7\u751F\u5371\u5BB3\u540E\u679C\u7684\uFF1B<br> \u2003\u20034\u3001\u4E3B\u52A8\u6539\u6B63\u6216\u8005\u53CA\u65F6\u4E2D\u6B62\u8FDD\u6CD5\u884C\u4E3A\u7684\uFF1B<br> \u2003\u20035\u3001\u79EF\u6781\u5C65\u884C\u73AF\u5883\u6C61\u67D3\u6C11\u4E8B\u635F\u5BB3\u8D54\u507F\u8D23\u4EFB\uFF0C\u6D88\u9664\u793E\u4F1A\u4E0D\u826F\u5F71\u54CD\u7684\uFF1B<br> \u2003\u20036\u3001\u8FDD\u6CD5\u884C\u4E3A\u53D1\u751F\u524D\uFF0C\u5DF2\u53CA\u65F6\u8DB3\u989D\u6295\u4FDD\u73AF\u5883\u6C61\u67D3\u8D23\u4EFB\u4FDD\u9669\u7684\uFF1B<br> \u2003\u20037\u3001\u53D7\u4ED6\u4EBA\u80C1\u8FEB\u6709\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u7684\uFF1B<br> \u2003\u20038\u3001\u6CD5\u5F8B\u6CD5\u89C4\u89C4\u7AE0\u89C4\u5B9A\u7684\u53EF\u4EE5\u4ECE\u8F7B\u5904\u7F5A\u7684\u5176\u4ED6\u60C5\u5F62\u3002</p><p id="t7">\u2003\u2003<b>\u7B2C\u4E03\u6761</b>\u2003\u9001\u8FBE\u884C\u653F\u5904\u7F5A\uFF08\u542C\u8BC1\uFF09\u544A\u77E5\u4E66\u540E\uFF0C\u5F53\u4E8B\u4EBA\u540C\u65F6\u6EE1\u8DB3\u4EE5\u4E0B\u60C5\u5F62\u7684\uFF0C\u53EF\u4EE5\u4E0D\u4E88\u884C\u653F\u5904\u7F5A\uFF1A<br> \u2003\u20031\u3001\u5E94\u7F16\u5236\u73AF\u5883\u5F71\u54CD\u62A5\u544A\u8868\u6216\u586B\u62A5\u73AF\u5883\u5F71\u54CD\u767B\u8BB0\u8868\uFF0C\u4E0D\u5C5E\u4E8E\u5316\u5B66\u539F\u6599\u548C\u5316\u5B66\u5236\u54C1\u5236\u9020\u3001\u533B\u836F\u5236\u9020\u3001\u7535\u9540\u3001\u97A3\u9769\u3001\u9020\u7EB8\u3001\u5236\u6D46\u3001\u91D1\u5C5E\u51B6\u70BC\u3001\u653E\u5C04\u6027\u3001\u5370\u67D3\u3001\u6D17\u6C34\u3001\u70BC\u7126\u3001\u70BC\u6CB9\u3001\u5371\u9669\u5E9F\u7269\u5904\u7F6E\u548C\u5229\u7528\u884C\u4E1A\u9879\u76EE\u7684\uFF1B<br> \u2003\u20032\u3001\u8FDD\u6CD5\u884C\u4E3A\u4E0D\u5B58\u5728\u672C\u89C4\u5B9A\u7B2C\u4E94\u6761\u7684\u60C5\u5F62\uFF0C\u4E14\u4E0D\u6D89\u53CA\u91CD\u91D1\u5C5E\u8D85\u6807\u6392\u653E\uFF0C\u4EE5\u53CA\u56FA\u4F53\u5E9F\u7269\uFF08\u542B\u5371\u9669\u5E9F\u7269\uFF09\u975E\u6CD5\u5904\u7F6E\u3001\u5229\u7528\u3001\u8FD0\u8F93\u7684\uFF1B<br> \u2003\u20033\u3001\u8FDD\u6CD5\u884C\u4E3A\u6CA1\u6709\u9020\u6210\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u6216\u6CD5\u5F8B\u6CD5\u89C4\u89C4\u5B9A\u7684\u5176\u4ED6\u4E25\u91CD\u60C5\u8282\u7684\uFF1B<br> \u2003\u20034\u3001\u4E0D\u5C5E\u4E8E\u571F\u58E4\u6C61\u67D3\u91CD\u70B9\u76D1\u7BA1\u884C\u4E1A\u3001\u5355\u4F4D\u7684\uFF1B<br> \u2003\u20035\u3001\u9001\u8FBE\u884C\u653F\u5904\u7F5A\u51B3\u5B9A\u4E66\u65F6\uFF0C\u5F53\u4E8B\u4EBA\u5DF2\u7ECF\u6CA1\u6709\u751F\u4EA7\u80FD\u529B\u7684\u3002<br> \u2003\u2003\u9001\u8FBE\u5C65\u884C\u884C\u653F\u51B3\u5B9A\u50AC\u544A\u4E66\u65F6\uFF0C\u5F53\u4E8B\u4EBA\u5DF2\u7ECF\u6CA1\u6709\u751F\u4EA7\u80FD\u529B\u7684\uFF0C\u53EF\u4EE5\u4E0D\u4E88\u52A0\u5904\u7F5A\u6B3E\u3002</p><p id="t8">\u2003\u2003<b>\u7B2C\u516B\u6761</b>\u2003\u5F53\u4E8B\u4EBA\u7684\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u4E0D\u5177\u6709\u4ECE\u91CD\u3001\u4ECE\u8F7B\u3001\u514D\u4E88\u5904\u7F5A\u60C5\u8282\uFF0C\u5E94\u5F53\u5BF9\u5176\u4E88\u4EE5\u9002\u4E2D\u7684\u5904\u7F5A\u3002</p><p id="t9">\u2003\u2003<b>\u7B2C\u4E5D\u6761</b>\u2003\u9002\u7528\u6CD5\u5F8B\u3001\u6CD5\u89C4\u3001\u89C4\u7AE0\u65F6\uFF0C\u9075\u5FAA\u4E0A\u4F4D\u6CD5\u4F18\u5148\u3001\u7279\u522B\u6CD5\u4F18\u5148\u3001\u65B0\u6CD5\u4F18\u5148\u539F\u5219\u3002<br> \u2003\u2003\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u53D1\u751F\u5728\u65B0\u6CD5\u65BD\u884C\u4EE5\u524D\uFF0C\u884C\u653F\u5904\u7F5A\u4F5C\u51FA\u5728\u65B0\u6CD5\u65BD\u884C\u4EE5\u540E\u7684\uFF0C\u5B9E\u4F53\u95EE\u9898\u9002\u7528\u65E7\u6CD5\u89C4\u5B9A\uFF0C\u7A0B\u5E8F\u95EE\u9898\u9002\u7528\u65B0\u6CD5\u89C4\u5B9A\uFF0C\u4F46\u4E0B\u5217\u60C5\u5F62\u9664\u5916\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u6CD5\u5F8B\u3001\u6CD5\u89C4\u6216\u89C4\u7AE0\u53E6\u6709\u89C4\u5B9A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u9002\u7528\u65B0\u6CD5\u5BF9\u4FDD\u62A4\u884C\u653F\u76F8\u5BF9\u4EBA\u7684\u5408\u6CD5\u6743\u76CA\u66F4\u4E3A\u6709\u5229\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u6309\u7167\u5177\u4F53\u884C\u653F\u884C\u4E3A\u7684\u6027\u8D28\u5E94\u5F53\u9002\u7528\u65B0\u6CD5\u7684\u5B9E\u4F53\u89C4\u5B9A\u7684\u3002</p><p id="t10">\u2003\u2003<b>\u7B2C\u5341\u6761</b>\u2003\u6CD5\u5F8B\u3001\u6CD5\u89C4\u3001\u89C4\u7AE0\u89C4\u5B9A\u7684\u5904\u7F5A\u79CD\u7C7B\u53EF\u4EE5\u5355\u5904\u6216\u53EF\u4EE5\u5E76\u5904\u7684\uFF0C\u53EF\u4EE5\u9009\u62E9\u9002\u7528\u3002\u89C4\u5B9A\u5E94\u5F53\u5E76\u5904\u7684\uFF0C\u4E0D\u5F97\u9009\u62E9\u9002\u7528\u3002<br> \u2003\u2003\u6848\u4EF6\u8C03\u67E5\u673A\u6784\u6267\u6CD5\u4EBA\u5458\u8C03\u67E5\u3001\u68C0\u67E5\u3001\u6536\u96C6\u8BC1\u636E\u65F6\uFF0C\u5E94\u5F53\u901A\u8FC7\u6587\u5B57\u3001\u7535\u5B50\u3001\u97F3\u50CF\u7B49\u8BB0\u5F55\u65B9\u5F0F\u5BF9\u6267\u6CD5\u8FC7\u7A0B\u8FDB\u884C\u8BB0\u5F55\u3002</p><p id="t11">\u2003\u2003<b>\u7B2C\u5341\u4E00\u6761</b>\u2003\u5B9E\u65BD\u884C\u653F\u5904\u7F5A\u524D\uFF0C\u751F\u6001\u73AF\u5883\u90E8\u95E8\u6267\u6CD5\u4EBA\u5458\u5E94\u5F53\u6536\u96C6\u80FD\u591F\u652F\u6491\u81EA\u7531\u88C1\u91CF\u6807\u51C6\u6863\u6B21\u7684\u8BC1\u636E\uFF0C\u5E76\u4EE5\u6B64\u4F5C\u4E3A\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u884C\u4F7F\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u7684\u4F9D\u636E\u3002<br> \u2003\u2003\u5F53\u4E8B\u4EBA\u540C\u65F6\u5B58\u5728\u591A\u4E2A\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\uFF0C\u7ECF\u8BA4\u5B9A\u5B58\u5728\u4E00\u5B9A\u8054\u7CFB\uFF0C\u4F46\u5404\u81EA\u72EC\u7ACB\u6784\u6210\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u7684\uFF0C\u5E94\u5F53\u5BF9\u6BCF\u4E2A\u8FDD\u6CD5\u884C\u4E3A\u5206\u522B\u4F9D\u6CD5\u7ED9\u4E88\u76F8\u5E94\u5904\u7F5A\u3002</p><p id="t12">\u2003\u2003<b>\u7B2C\u5341\u4E8C\u6761</b>\u2003\u7B26\u5408\u300A\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u73AF\u5883\u4FDD\u62A4\u6CD5\u300B\u3001\u300A\u884C\u653F\u4E3B\u7BA1\u90E8\u95E8\u79FB\u9001\u9002\u7528\u884C\u653F\u62D8\u7559\u73AF\u5883\u8FDD\u6CD5\u6848\u4EF6\u6682\u884C\u529E\u6CD5\u300B\u3001\u300A\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5B9E\u65BD\u67E5\u5C01\u3001\u6263\u62BC\u529E\u6CD5\u300B\u7B49\u6709\u5173\u79FB\u9001\u516C\u5B89\u673A\u5173\u5904\u4EE5\u884C\u653F\u62D8\u7559\u7B49\u7684\u89C4\u5B9A\u7684\uFF0C\u5E94\u5F53\u53CA\u65F6\u79FB\u9001\u516C\u5B89\u673A\u5173\u5904\u7406\u3002</p><p id="t13">\u2003\u2003<b>\u7B2C\u5341\u4E09\u6761</b>\u2003\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u6D89\u5ACC\u72AF\u7F6A\u7684\uFF0C\u5E94\u5F53\u53CA\u65F6\u4F9D\u6CD5\u79FB\u9001\u516C\u5B89\u673A\u5173\uFF0C\u4E0D\u5F97\u4EE5\u884C\u653F\u5904\u7F5A\u4EE3\u66FF\u5211\u4E8B\u5904\u7F5A\u3002<br> \u2003\u2003\u5411\u516C\u5B89\u673A\u5173\u79FB\u9001\u6D89\u5ACC\u73AF\u5883\u72AF\u7F6A\u6848\u4EF6\uFF0C\u5DF2\u4F5C\u51FA\u7684\u8B66\u544A\u3001\u8D23\u4EE4\u505C\u4EA7\u505C\u4E1A\u3001\u6682\u6263\u6216\u8005\u540A\u9500\u8BB8\u53EF\u8BC1\u7684\u884C\u653F\u5904\u7F5A\u51B3\u5B9A\uFF0C\u4E0D\u505C\u6B62\u6267\u884C\u3002\u672A\u4F5C\u51FA\u884C\u653F\u5904\u7F5A\u51B3\u5B9A\u7684\uFF0C\u539F\u5219\u4E0A\u5E94\u5F53\u5728\u516C\u5B89\u673A\u5173\u51B3\u5B9A\u4E0D\u4E88\u7ACB\u6848\u6216\u8005\u64A4\u9500\u6848\u4EF6\u3001\u4EBA\u6C11\u68C0\u5BDF\u9662\u4F5C\u51FA\u4E0D\u8D77\u8BC9\u51B3\u5B9A\u3001\u4EBA\u6C11\u6CD5\u9662\u4F5C\u51FA\u65E0\u7F6A\u5224\u51B3\u6216\u8005\u514D\u4E88\u5211\u4E8B\u5904\u7F5A\u540E\uFF0C\u518D\u51B3\u5B9A\u662F\u5426\u7ED9\u4E88\u884C\u653F\u5904\u7F5A\u3002\u6D89\u5ACC\u72AF\u7F6A\u6848\u4EF6\u7684\u79FB\u9001\u529E\u7406\u671F\u95F4\uFF0C\u4E0D\u8BA1\u5165\u884C\u653F\u5904\u7F5A\u671F\u9650\u3002</p><p id="t14">\u2003\u2003<b>\u7B2C\u5341\u56DB\u6761</b>\u2003\u300A\u6C5F\u95E8\u5E02\u751F\u6001\u73AF\u5883\u5C40\u5173\u4E8E\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u88C1\u91CF\u6807\u51C6\u53C2\u7167\u8868\u300B\u662F\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u884C\u4F7F\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u7684\u5177\u4F53\u6807\u51C6\u3002\u5F53\u4E8B\u4EBA\u7684\u8FDD\u6CD5\u884C\u4E3A\u540C\u65F6\u5177\u6709\u9644\u4EF6\u89C4\u5B9A\u7684\u4E0D\u540C\u6863\u6B21\u6240\u5217\u88C1\u91CF\u60C5\u8282\u7684\uFF0C\u6309\u8F83\u91CD\u6863\u6B21\u7684\u88C1\u91CF\u60C5\u8282\u8FDB\u884C\u5904\u7F5A\u3002</p><p id="t15">\u2003\u2003<b>\u7B2C\u5341\u4E94\u6761</b>\u2003\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u6279\u51C6\u540E\uFF0C\u5728\u5EFA\u8BBE\u8FC7\u7A0B\u4E2D\u6216\u8005\u4F9D\u6CD5\u6295\u4EA7\u540E\u9879\u76EE\u5185\u5BB9\u53D1\u751F\u53D8\u5316\u7684\uFF0C\u5BF9\u5176\u662F\u5426\u5C5E\u4E8E\u91CD\u5927\u53D8\u52A8\u7684\u8BA4\u5B9A\u53C2\u7167\u56FD\u5BB6\u751F\u6001\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5370\u53D1\u7684\u5EFA\u8BBE\u9879\u76EE\u91CD\u5927\u53D8\u52A8\u6E05\u5355\u6709\u5173\u6587\u4EF6\u4EE5\u53CA\u539F\u73AF\u5883\u4FDD\u62A4\u90E8\u300A\u5173\u4E8E\u5EFA\u8BBE\u9879\u76EE\u201C\u672A\u6279\u5148\u5EFA\u201D\u8FDD\u6CD5\u884C\u4E3A\u6CD5\u5F8B\u9002\u7528\u95EE\u9898\u7684\u610F\u89C1\u300B\uFF08\u73AF\u653F\u6CD5\u51FD\u30142018\u301531\u53F7\uFF09\u3001\u300A\u5173\u4E8E\u52A0\u5F3A\u201C\u672A\u6279\u5148\u5EFA\u201D\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u7BA1\u7406\u5DE5\u4F5C\u7684\u901A\u77E5\u300B\uFF08\u73AF\u529E\u73AF\u8BC4\u30142018\u301518\u53F7\uFF09\u6267\u884C\u3002</p><p id="t16">\u2003\u2003<b>\u7B2C\u5341\u516D\u6761</b>\u2003\u672C\u89C4\u5B9A\u53CA\u300A\u6C5F\u95E8\u5E02\u751F\u6001\u73AF\u5883\u5C40\u5173\u4E8E\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u88C1\u91CF\u6807\u51C6\u53C2\u7167\u8868\u300B\u7684 \u201C\u4EE5\u4E0A\u201D\u3001\u201C\u4EE5\u4E0B\u201D\u5747\u542B\u672C\u6570\u3002<br> \u2003\u2003\u201C\u4E24\u5E74\u5185\u6B21\u6570\u201D\u5747\u6307\u53D7\u5230\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u884C\u653F\u5904\u7F5A\u7684\u6B21\u6570\u3002<br> \u2003\u2003\u201C\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u201D\u7B49\u7EA7\u5206\u7C7B\u6267\u884C\u300A\u56FD\u52A1\u9662\u529E\u516C\u5385\u5173\u4E8E\u5370\u53D1\u56FD\u5BB6\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5E94\u6025\u9884\u6848\u7684\u901A\u77E5\u300B\uFF08\u56FD\u529E\u51FD\u30142014\u3015119\u53F7\uFF09\u6709\u5173\u89C4\u5B9A\u3002<br> \u2003\u2003\u201C\u6709\u6BD2\u6709\u5BB3\u7269\u8D28\u201D\u6307\u300A\u6700\u9AD8\u4EBA\u6C11\u6CD5\u9662 \u6700\u9AD8\u4EBA\u6C11\u68C0\u5BDF\u9662\u5173\u4E8E\u529E\u7406\u73AF\u5883\u6C61\u67D3\u5211\u4E8B\u6848\u4EF6\u9002\u7528\u6CD5\u5F8B\u82E5\u5E72\u95EE\u9898\u7684\u89E3\u91CA\u300B\uFF08\u6CD5\u91CA\u30142016\u301529\u53F7\uFF09\u7B2C\u5341\u4E94\u6761\u201C\u4E0B\u5217\u7269\u8D28\u5E94\u5F53\u8BA4\u5B9A\u4E3A\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u89C4\u5B9A\u7684\u201C\u6709\u6BD2\u7269\u8D28\u201D\uFF1A\uFF08\u4E00\uFF09\u5371\u9669\u5E9F\u7269\uFF0C\u662F\u6307\u5217\u5165\u56FD\u5BB6\u5371\u9669\u5E9F\u7269\u540D\u5F55\uFF0C\u6216\u8005\u6839\u636E\u56FD\u5BB6\u89C4\u5B9A\u7684\u5371\u9669\u5E9F\u7269\u9274\u522B\u6807\u51C6\u548C\u9274\u522B\u65B9\u6CD5\u8BA4\u5B9A\u7684\uFF0C\u5177\u6709\u5371\u9669\u7279\u6027\u7684\u5E9F\u7269\uFF1B\uFF08\u4E8C\uFF09\u300A\u5173\u4E8E\u6301\u4E45\u6027\u6709\u673A\u6C61\u67D3\u7269\u7684\u65AF\u5FB7\u54E5\u5C14\u6469\u516C\u7EA6\u300B\u9644\u4EF6\u6240\u5217\u7269\u8D28\uFF1B\uFF08\u4E09\uFF09\u542B\u91CD\u91D1\u5C5E\u7684\u6C61\u67D3\u7269\uFF1B\uFF08\u56DB\uFF09\u5176\u4ED6\u5177\u6709\u6BD2\u6027\uFF0C\u53EF\u80FD\u6C61\u67D3\u73AF\u5883\u7684\u7269\u8D28\u201D\u6240\u89C4\u5B9A\u7684\u7269\u8D28\uFF0C\u4EE5\u53CA\u7EB3\u5165\u300A\u6709\u6BD2\u6709\u5BB3\u5927\u6C14\u6C61\u67D3\u7269\u540D\u5F55\uFF082018\u5E74\uFF09\u300B\uFF08\u751F\u6001\u73AF\u5883\u90E8\u3001\u536B\u751F\u5065\u5EB7\u59D4\u516C\u544A2019\u5E74\u7B2C4\u53F7\uFF09\u3001\u300A\u6709\u6BD2\u6709\u5BB3\u6C34\u6C61\u67D3\u7269\u540D\u5F55\uFF08\u7B2C\u4E00\u6279\uFF09\u300B\uFF08\u751F\u6001\u73AF\u5883\u90E8\u3001\u536B\u751F\u5065\u5EB7\u59D4\u516C\u544A2019\u5E74\u7B2C28\u53F7\uFF09\u7684\u7269\u8D28\uFF0C\u4EE5\u53CA\u5176\u4ED6\u6CD5\u5F8B\u6CD5\u89C4\u6216\u56FD\u5BB6\u3001\u7701\u6709\u5173\u6587\u4EF6\u6240\u89C4\u5B9A\u7684\u7269\u8D28\u3002</p><p id="t17">\u2003\u2003<b>\u7B2C\u5341\u4E03\u6761</b>\u2003\u884C\u653F\u5904\u7F5A\u4F5C\u51FA\u540E\uFF0C\u53D1\u73B0\u786E\u6709\u9519\u8BEF\u7684\uFF0C\u751F\u6001\u73AF\u5883\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u91C7\u53D6\u8865\u6B63\u3001\u64A4\u56DE\u3001\u91CD\u4F5C\u7B49\u8DB3\u4EE5\u7EA0\u6B63\u7684\u63AA\u65BD\u4E3B\u52A8\u6539\u6B63\u3002</p><p id="t18">\u2003\u2003<b>\u7B2C\u5341\u516B\u6761</b>\u2003\u672C\u89C4\u5B9A\u81EA2020\u5E743\u67081\u6708\u8D77\u65BD\u884C\uFF0C\u6709\u6548\u671F\u4E09\u5E74\u3002\u539F\u300A\u6C5F\u95E8\u5E02\u73AF\u5883\u4FDD\u62A4\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u88C1\u91CF\u6807\u51C6(\u8BD5\u884C)\u300B\u5E9F\u6B62\u3002</p>',19),d={class:"custom-container info"},l=t("p",{class:"custom-container-title"},"\u9644\u4EF6",-1),h={href:"/static/doc/P5/jmzyclb.xls",target:"_blank",rel:"noopener noreferrer"},_=b("\u6C5F\u95E8\u5E02\u751F\u6001\u73AF\u5883\u5C40\u5173\u4E8E\u751F\u6001\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u884C\u653F\u5904\u7F5A\u81EA\u7531\u88C1\u91CF\u6743\u88C1\u91CF\u6807\u51C6\u53C2\u7167\u8868"),f=t("hr",null,null,-1),m={class:"custom-container warning"},j=t("p",{class:"custom-container-title"},"\u539F\u6587\u94FE\u63A5",-1),u={href:"http://www.jiangmen.gov.cn/bmpd/jmssthjj/zwgk/bmwj/content/post_1982219.html",target:"_blank",rel:"noopener noreferrer"},w=b("http://www.jiangmen.gov.cn/bmpd/jmssthjj/zwgk/bmwj/content/post_1982219.html");function g(x,z){const r=c("ExternalLinkIcon");return s(),o("div",null,[i,t("div",d,[l,t("p",null,[t("a",h,[_,e(r)])])]),f,t("div",m,[j,t("p",null,[t("a",u,[w,e(r)])])])])}var y=a(p,[["render",g],["__file","jmssthjjgysthjwfxwxzcfzyclqclbzgd.html.vue"]]);export{y as default};
