import{_ as b}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as a,a as t,b as p,e as i,d as o,r as s}from"./app.8e263a3f.js";const n={},d=i('<details class="custom-container details"><summary>\u76EE\u5F55</summary><ul><li><p>\u7B2C1~21\u6761\uFF1A</p><p><a href="#t1">1</a>\u2003<a href="#t2">2</a>\u2003<a href="#t3">3</a>\u2003<a href="#t4">4</a>\u2003<a href="#t5">5</a>\u2003<a href="#t6">6</a>\u2003<a href="#t7">7</a>\u2003<a href="#t8">8</a>\u2003<a href="#t9">9</a>\u2003<a href="#t10">10</a>\u2003<a href="#t11">11</a>\u2003<a href="#t12">12</a>\u2003<a href="#t13">13</a>\u2003<a href="#t14">14</a>\u2003<a href="#t15">15</a>\u2003<a href="#t16">16</a>\u2003<a href="#t17">17</a>\u2003<a href="#t18">18</a>\u2003<a href="#t19">19</a>\u2003<a href="#t20">20</a>\u2003<a href="#t21">21</a></p></li></ul></details><p id="t1">\u2003\u2003<b>\u7B2C\u4E00\u6761</b>\u2003\u4E3A\u7763\u4FC3\u5730\u65B9\u4EBA\u6C11\u653F\u5E9C\u5C65\u884C\u73AF\u5883\u4FDD\u62A4\u8D23\u4EFB\uFF0C\u96C6\u4E2D\u89E3\u51B3\u7A81\u51FA\u73AF\u5883\u95EE\u9898\uFF0C\u63A8\u52A8\u533A\u57DF\u73AF\u5883\u8D28\u91CF\u6539\u5584\uFF0C\u6839\u636E\u300A\u73AF\u5883\u4FDD\u62A4\u6CD5\u300B\u300A\u6C34\u6C61\u67D3\u9632\u6CBB\u6CD5\u300B\u300A\u5927\u6C14\u6C61\u67D3\u9632\u6CBB\u6CD5\u300B\u300A\u89C4\u5212\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6761\u4F8B\u300B\u7B49\u6CD5\u5F8B\u6CD5\u89C4\uFF0C\u4EE5\u53CA\u300A\u5173\u4E8E\u52A0\u5FEB\u63A8\u8FDB\u751F\u6001\u6587\u660E\u5EFA\u8BBE\u7684\u610F\u89C1\u300B\u300A\u5173\u4E8E\u843D\u5B9E\u79D1\u5B66\u53D1\u5C55\u89C2\u52A0\u5F3A\u73AF\u5883\u4FDD\u62A4\u7684\u51B3\u5B9A\u300B\u300A\u6C34\u6C61\u67D3\u9632\u6CBB\u884C\u52A8\u8BA1\u5212\u300B\u300A\u5927\u6C14\u6C61\u67D3\u9632\u6CBB\u884C\u52A8\u8BA1\u5212\u300B\u7B49\u6587\u4EF6\u6709\u5173\u533A\u57DF\u9650\u6279\u7684\u89C4\u5B9A\uFF0C\u5236\u5B9A\u672C\u529E\u6CD5\u3002</p><p id="t2">\u2003\u2003<b>\u7B2C\u4E8C\u6761</b>\u2003\u672C\u529E\u6CD5\u9002\u7528\u4E8E\u73AF\u5883\u4FDD\u62A4\u90E8\u5B9E\u65BD\u7684\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u533A\u57DF\u9650\u6279\u3002<br> \u2003\u2003\u7701\u7EA7\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u5B9E\u65BD\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u533A\u57DF\u9650\u6279\uFF0C\u53C2\u7167\u672C\u529E\u6CD5\u6267\u884C\u3002</p><p id="t3">\u2003\u2003<b>\u7B2C\u4E09\u6761</b>\u2003\u6709\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\u5730\u533A\uFF0C\u73AF\u5883\u4FDD\u62A4\u90E8\u6682\u505C\u5BA1\u6279\u6709\u5173\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u5BF9\u5728\u89C4\u5B9A\u671F\u9650\u5185\u672A\u5B8C\u6210\u56FD\u5BB6\u786E\u5B9A\u7684\u6C34\u73AF\u5883\u8D28\u91CF\u6539\u5584\u76EE\u6807\u3001\u5927\u6C14\u73AF\u5883\u8D28\u91CF\u6539\u5584\u76EE\u6807\u3001\u571F\u58E4\u73AF\u5883\u8D28\u91CF\u8003\u6838\u76EE\u6807\u7684\u5730\u533A\uFF0C\u6682\u505C\u5BA1\u6279\u65B0\u589E\u6392\u653E\u91CD\u70B9\u6C61\u67D3\u7269\u7684\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002<br> \u2003\u2003\uFF08\u4E8C\uFF09\u5BF9\u672A\u5B8C\u6210\u4E0A\u4E00\u5E74\u5EA6\u56FD\u5BB6\u786E\u5B9A\u7684\u91CD\u70B9\u6C34\u6C61\u67D3\u7269\u3001\u5927\u6C14\u6C61\u67D3\u7269\u6392\u653E\u603B\u91CF\u63A7\u5236\u6307\u6807\u7684\u5730\u533A\uFF0C\u6216\u8005\u672A\u5B8C\u6210\u56FD\u5BB6\u786E\u5B9A\u7684\u91CD\u70B9\u91CD\u91D1\u5C5E\u6C61\u67D3\u7269\u6392\u653E\u91CF\u63A7\u5236\u76EE\u6807\u7684\u5730\u533A\uFF0C\u6682\u505C\u5BA1\u6279\u65B0\u589E\u6392\u653E\u91CD\u70B9\u6C61\u67D3\u7269\u7684\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002<br> \u2003\u2003\uFF08\u4E09\uFF09\u5BF9\u751F\u6001\u7834\u574F\u4E25\u91CD\u6216\u8005\u5C1A\u672A\u5B8C\u6210\u751F\u6001\u6062\u590D\u4EFB\u52A1\u7684\u5730\u533A\uFF0C\u6682\u505C\u5BA1\u6279\u5BF9\u751F\u6001\u6709\u8F83\u5927\u5F71\u54CD\u7684\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002<br> \u2003\u2003\uFF08\u56DB\uFF09\u5BF9\u8FDD\u53CD\u4E3B\u4F53\u529F\u80FD\u533A\u5B9A\u4F4D\u3001\u7A81\u7834\u8D44\u6E90\u73AF\u5883\u751F\u6001\u4FDD\u62A4\u7EA2\u7EBF\u3001\u8D85\u8FC7\u8D44\u6E90\u6D88\u8017\u548C\u73AF\u5883\u5BB9\u91CF\u627F\u8F7D\u80FD\u529B\u7684\u5730\u533A\uFF0C\u6682\u505C\u5BA1\u6279\u5BF9\u751F\u6001\u6709\u8F83\u5927\u5F71\u54CD\u7684\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002<br> \u2003\u2003\uFF08\u4E94\uFF09\u5BF9\u672A\u4F9D\u6CD5\u5F00\u5C55\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u5373\u7EC4\u7EC7\u5B9E\u65BD\u5F00\u53D1\u5EFA\u8BBE\u89C4\u5212\u7684\u5730\u533A\uFF0C\u6682\u505C\u5BA1\u6279\u5BF9\u751F\u6001\u6709\u8F83\u5927\u5F71\u54CD\u7684\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002<br> \u2003\u2003\uFF08\u516D\uFF09\u5176\u4ED6\u6CD5\u5F8B\u6CD5\u89C4\u548C\u56FD\u52A1\u9662\u89C4\u5B9A\u8981\u6C42\u5B9E\u65BD\u533A\u57DF\u9650\u6279\u7684\u60C5\u5F62\u3002</p><p id="t4">\u2003\u2003<b>\u7B2C\u56DB\u6761</b>\u2003\u73AF\u5883\u4FDD\u62A4\u90E8\u4E3B\u7BA1\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u7684\u673A\u6784\uFF08\u4EE5\u4E0B\u7B80\u79F0\u73AF\u8BC4\u7BA1\u7406\u673A\u6784\uFF09\u8D1F\u8D23\u533A\u57DF\u9650\u6279\u7684\u5F52\u53E3\u7BA1\u7406\u548C\u7EC4\u7EC7\u5B9E\u65BD\uFF0C\u6C47\u603B\u9650\u6279\u5EFA\u8BAE\uFF0C\u529E\u7406\u62A5\u5BA1\u624B\u7EED\uFF0C\u8D77\u8349\u9650\u6279\u51B3\u5B9A\u6587\u4E66\uFF0C\u7EC4\u7EC7\u5B9E\u65BD\u533A\u57DF\u9650\u6279\u51B3\u5B9A\uFF0C\u5E76\u76D1\u7763\u6307\u5BFC\u5730\u65B9\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u843D\u5B9E\u533A\u57DF\u9650\u6279\u7BA1\u7406\u8981\u6C42\u3002<br> \u2003\u2003\u73AF\u5883\u4FDD\u62A4\u90E8\u4E3B\u7BA1\u6C61\u67D3\u9632\u6CBB\u3001\u751F\u6001\u4FDD\u62A4\u7B49\u5DE5\u4F5C\u7684\u7BA1\u7406\u673A\u6784\uFF08\u4EE5\u4E0B\u7B80\u79F0\u76F8\u5173\u7BA1\u7406\u673A\u6784\uFF09\u8D1F\u8D23\u8BA4\u5B9A\u9650\u6279\u60C5\u5F62\uFF0C\u63D0\u51FA\u9650\u6279\u5EFA\u8BAE\uFF0C\u4EE5\u53CA\u9650\u6279\u671F\u95F4\u7684\u6574\u6539\u7763\u67E5\u548C\u73B0\u573A\u6838\u67E5\u3002</p><p id="t5">\u2003\u2003<b>\u7B2C\u4E94\u6761</b>\u2003\u533A\u57DF\u9650\u6279\u6309\u7167\u4E0B\u5217\u7A0B\u5E8F\u7EC4\u7EC7\u5B9E\u65BD\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09 \u8BA4\u5B9A\u9650\u6279\u60C5\u5F62\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09 \u4E0B\u8FBE\u9650\u6279\u51B3\u5B9A\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09 \u6574\u6539\u7763\u67E5\u548C\u73B0\u573A\u6838\u67E5\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09 \u89E3\u9664\u9650\u6279\u3002</p><p id="t6">\u2003\u2003<b>\u7B2C\u516D\u6761</b>\u2003\u76F8\u5173\u7BA1\u7406\u673A\u6784\u901A\u8FC7\u65E5\u5E38\u7BA1\u7406\u3001\u76D1\u7763\u68C0\u67E5\u3001\u4E13\u9879\u68C0\u67E5\u3001\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u8C03\u67E5\u5904\u7406\u53CA\u4E3E\u62A5\u7B49\u9014\u5F84\uFF0C\u53D1\u73B0\u5B58\u5728\u672C\u529E\u6CD5\u7B2C\u4E09\u6761\u89C4\u5B9A\u7684\u9650\u6279\u60C5\u5F62\u7684\uFF0C\u5E94\u5F53\u8FDB\u884C\u8C03\u67E5\u53D6\u8BC1\uFF0C\u8BA4\u5B9A\u76F8\u5173\u4E8B\u5B9E\uFF0C\u5E76\u63D0\u51FA\u9650\u6279\u533A\u57DF\u3001\u9650\u6279\u5185\u5BB9\u3001\u9650\u6279\u671F\u9650\u3001\u6574\u6539\u8981\u6C42\u7B49\u5EFA\u8BAE\u3002\u9650\u6279\u5EFA\u8BAE\u53CA\u9650\u6279\u60C5\u5F62\u8BA4\u5B9A\u62A5\u544A\u5E94\u5F53\u8F6C\u73AF\u8BC4\u7BA1\u7406\u673A\u6784\u3002</p><p id="t7">\u2003\u2003<b>\u7B2C\u4E03\u6761</b>\u2003\u9650\u6279\u5EFA\u8BAE\u5E94\u5F53\u89C6\u5177\u4F53\u60C5\u51B5\u786E\u5B9A\u4E09\u4E2A\u6708\u81F3\u5341\u4E8C\u4E2A\u6708\u7684\u9650\u6279\u671F\u9650\u3002</p><p id="t8">\u2003\u2003<b>\u7B2C\u516B\u6761</b>\u2003\u73AF\u8BC4\u7BA1\u7406\u673A\u6784\u6536\u5230\u9650\u6279\u5EFA\u8BAE\u540E\uFF0C\u6C47\u603B\u5F62\u6210\u9650\u6279\u610F\u89C1\uFF0C\u62A5\u8BF7\u90E8\u5E38\u52A1\u4F1A\u5BA1\u8BAE\u901A\u8FC7\u540E\uFF0C\u8D77\u8349\u9650\u6279\u51B3\u5B9A\u6587\u4E66\uFF0C\u5E76\u6309\u7A0B\u5E8F\u4E0B\u8FBE\u88AB\u9650\u6279\u5730\u533A\u3002<br> \u2003\u2003\u9650\u6279\u51B3\u5B9A\u5E94\u5F53\u5305\u62EC\u4EE5\u4E0B\u5185\u5BB9\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u9650\u6279\u533A\u57DF\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u9650\u6279\u60C5\u5F62\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u9650\u6279\u5185\u5BB9\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09\u9650\u6279\u671F\u9650\uFF1B<br> \u2003\u2003\uFF08\u4E94\uFF09\u6574\u6539\u8981\u6C42\uFF1B<br> \u2003\u2003\uFF08\u516D\uFF09\u76D1\u7763\u68C0\u67E5\u8981\u6C42\u3002</p><p id="t9">\u2003\u2003<b>\u7B2C\u4E5D\u6761</b>\u2003\u73AF\u8BC4\u7BA1\u7406\u673A\u6784\u81EA\u9650\u6279\u51B3\u5B9A\u4E0B\u8FBE\u4E4B\u65E5\u8D77\u5373\u6682\u505C\u5BA1\u6279\u88AB\u9650\u6279\u5730\u533A\u7684\u76F8\u5173\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002<br> \u2003\u2003\u5730\u65B9\u5404\u7EA7\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u5E94\u5F53\u540C\u6B65\u6682\u505C\u5BA1\u6279\u88AB\u9650\u6279\u5730\u533A\u7684\u76F8\u5173\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002<br> \u2003\u2003\u63D0\u51FA\u9650\u6279\u5EFA\u8BAE\u7684\u76F8\u5173\u7BA1\u7406\u673A\u6784\u8D1F\u8D23\u9650\u6279\u671F\u95F4\u7684\u76D1\u7763\u68C0\u67E5\u3002</p><p id="t10">\u2003\u2003<b>\u7B2C\u5341\u6761</b>\u2003\u5B9E\u65BD\u533A\u57DF\u9650\u6279\u671F\u95F4\uFF0C\u88AB\u9650\u6279\u5730\u533A\u7684\u5730\u65B9\u4EBA\u6C11\u653F\u5E9C\u5E94\u5F53\u5236\u8BA2\u6574\u6539\u65B9\u6848\uFF0C\u660E\u786E\u6574\u6539\u8FDB\u5EA6\uFF0C\u5168\u9762\u843D\u5B9E\u6574\u6539\u8981\u6C42\uFF0C\u5E76\u5411\u4F5C\u51FA\u9650\u6279\u51B3\u5B9A\u7684\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u62A5\u9001\u6574\u6539\u7ED3\u679C\u62A5\u544A\u3002<br> \u2003\u2003\u56E0\u672C\u529E\u6CD5\u7B2C\u4E09\u6761\u7B2C\u4E00\u3001\u4E09\u3001\u56DB\u9879\u60C5\u5F62\u88AB\u9650\u6279\u7684\u5730\u533A\uFF0C\u5730\u65B9\u4EBA\u6C11\u653F\u5E9C\u5E94\u5F53\u5728\u5B8C\u6210\u6574\u6539\u540E\uFF0C\u7EC4\u7EC7\u5BF9\u88AB\u9650\u6279\u5730\u533A\u7684\u533A\u57DF\u73AF\u5883\u8D28\u91CF\u6539\u5584\u60C5\u51B5\u8FDB\u884C\u8BC4\u4F30\uFF0C\u5F62\u6210\u4E66\u9762\u62A5\u544A\uFF0C\u5E76\u7EB3\u5165\u524D\u6B3E\u89C4\u5B9A\u7684\u6574\u6539\u7ED3\u679C\u62A5\u544A\uFF0C\u62A5\u9001\u4F5C\u51FA\u9650\u6279\u51B3\u5B9A\u7684\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u3002</p><p id="t11">\u2003\u2003<b>\u7B2C\u5341\u4E00\u6761</b>\u2003\u9650\u6279\u671F\u9650\u5C4A\u6EE1\u540E\u4E00\u4E2A\u6708\u5185\uFF0C\u63D0\u51FA\u9650\u6279\u5EFA\u8BAE\u7684\u76F8\u5173\u7BA1\u7406\u673A\u6784\u5E94\u5F53\u4F1A\u540C\u73AF\u5883\u4FDD\u62A4\u533A\u57DF\u7763\u67E5\u4E2D\u5FC3\u7EC4\u7EC7\u73B0\u573A\u6838\u67E5\uFF0C\u63D0\u51FA\u73B0\u573A\u6838\u67E5\u62A5\u544A\u3002<br> \u2003\u2003\u5BF9\u5168\u9762\u843D\u5B9E\u6574\u6539\u8981\u6C42\u7684\u5730\u533A\uFF0C\u76F8\u5173\u7BA1\u7406\u673A\u6784\u5E94\u5F53\u63D0\u51FA\u89E3\u9664\u9650\u6279\u5EFA\u8BAE\uFF1B\u5BF9\u672A\u843D\u5B9E\u6574\u6539\u8981\u6C42\u7684\u5730\u533A\uFF0C\u76F8\u5173\u7BA1\u7406\u673A\u6784\u5E94\u5F53\u63D0\u51FA\u5EF6\u957F\u9650\u6279\u5EFA\u8BAE\u3002<br> \u2003\u2003\u89E3\u9664\u9650\u6279\u5EFA\u8BAE\u3001\u5EF6\u957F\u9650\u6279\u5EFA\u8BAE\u548C\u73B0\u573A\u6838\u67E5\u62A5\u544A\u5E94\u5F53\u8F6C\u73AF\u8BC4\u7BA1\u7406\u673A\u6784\u3002</p><p id="t12">\u2003\u2003<b>\u7B2C\u5341\u4E8C\u6761</b>\u2003\u73AF\u8BC4\u7BA1\u7406\u673A\u6784\u6536\u5230\u89E3\u9664\u9650\u6279\u5EFA\u8BAE\u540E\uFF0C\u6C47\u603B\u5F62\u6210\u89E3\u9664\u9650\u6279\u610F\u89C1\uFF0C\u62A5\u8BF7\u90E8\u5E38\u52A1\u4F1A\u5BA1\u8BAE\u901A\u8FC7\u540E\uFF0C\u8D77\u8349\u89E3\u9664\u9650\u6279\u51B3\u5B9A\u6587\u4E66\uFF0C\u5E76\u6309\u7A0B\u5E8F\u4E0B\u8FBE\u88AB\u9650\u6279\u5730\u533A\u3002<br> \u2003\u2003\u89E3\u9664\u9650\u6279\u51B3\u5B9A\u5305\u62EC\u4EE5\u4E0B\u5185\u5BB9\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u6574\u6539\u843D\u5B9E\u60C5\u51B5\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u73B0\u573A\u6838\u67E5\u7ED3\u679C\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u89E3\u9664\u9650\u6279\u610F\u89C1\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09\u540E\u7EED\u76D1\u7BA1\u8981\u6C42\u3002</p><p id="t13">\u2003\u2003<b>\u7B2C\u5341\u4E09\u6761</b>\u2003\u73AF\u8BC4\u7BA1\u7406\u673A\u6784\u6536\u5230\u5EF6\u957F\u9650\u6279\u5EFA\u8BAE\u540E\uFF0C\u6C47\u603B\u5F62\u6210\u5EF6\u957F\u9650\u6279\u610F\u89C1\uFF0C\u62A5\u8BF7\u90E8\u5E38\u52A1\u4F1A\u5BA1\u8BAE\u901A\u8FC7\u540E\uFF0C\u8D77\u8349\u5EF6\u957F\u9650\u6279\u51B3\u5B9A\u6587\u4E66\uFF0C\u5E76\u6309\u7A0B\u5E8F\u4E0B\u8FBE\u88AB\u9650\u6279\u5730\u533A\u3002\u5EF6\u957F\u9650\u6279\u671F\u9650\u6700\u957F\u4E0D\u8D85\u8FC7\u516D\u4E2A\u6708\u3002<br> \u2003\u2003\u5EF6\u957F\u9650\u6279\u51B3\u5B9A\u5305\u62EC\u4EE5\u4E0B\u5185\u5BB9\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09 \u6574\u6539\u5B58\u5728\u7684\u95EE\u9898\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09 \u73B0\u573A\u6838\u67E5\u7ED3\u679C\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09 \u5EF6\u957F\u9650\u6279\u5185\u5BB9\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09 \u5EF6\u957F\u9650\u6279\u671F\u9650\uFF1B<br> \u2003\u2003\uFF08\u4E94\uFF09 \u6574\u6539\u8981\u6C42\u3002</p><p id="t14">\u2003\u2003<b>\u7B2C\u5341\u56DB\u6761</b>\u2003\u73AF\u5883\u4FDD\u62A4\u90E8\u4F5C\u51FA\u7684\u9650\u6279\u3001\u89E3\u9664\u9650\u6279\u3001\u5EF6\u957F\u9650\u6279\u7B49\u51B3\u5B9A\u5E94\u5F53\u540C\u65F6\u6284\u9001\u88AB\u9650\u6279\u5730\u533A\u76F8\u5173\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u3002</p><p id="t15">\u2003\u2003<b>\u7B2C\u5341\u4E94\u6761</b>\u2003\u7701\u7EA7\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u4F5C\u51FA\u7684\u9650\u6279\u3001\u89E3\u9664\u9650\u6279\u3001\u5EF6\u957F\u9650\u6279\u7B49\u51B3\u5B9A\uFF0C\u5E94\u5F53\u540C\u65F6\u6284\u62A5\u73AF\u5883\u4FDD\u62A4\u90E8\u3002<br> \u2003\u2003\u73AF\u5883\u4FDD\u62A4\u90E8\u5BF9\u7701\u7EA7\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u5B9E\u65BD\u9650\u6279\u7684\u5730\u533A\uFF0C\u5E94\u5F53\u540C\u6B65\u6682\u505C\u5BA1\u6279\u76F8\u5173\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u3002</p><p id="t16">\u2003\u2003<b>\u7B2C\u5341\u516D\u6761</b>\u2003\u9650\u6279\u3001\u89E3\u9664\u9650\u6279\u3001\u5EF6\u957F\u9650\u6279\u7B49\u51B3\u5B9A\u5E94\u5F53\u901A\u8FC7\u653F\u5E9C\u7F51\u7AD9\u3001\u62A5\u7EB8\u7B49\u5A92\u4F53\u5E73\u53F0\u5411\u793E\u4F1A\u516C\u5F00\uFF0C\u63A5\u53D7\u793E\u4F1A\u76D1\u7763\u3002</p><p id="t17">\u2003\u2003<b>\u7B2C\u5341\u4E03\u6761</b>\u2003\u5BF9\u672A\u6267\u884C\u540C\u6B65\u9650\u6279\u8981\u6C42\u7684\u5730\u65B9\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u5BA1\u6279\u7684\u5EFA\u8BBE\u9879\u76EE\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\uFF0C\u4E0A\u7EA7\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u5E94\u5F53\u8D23\u4EE4\u5176\u64A4\u9500\u8BE5\u5BA1\u6279\u51B3\u5B9A\uFF1B\u62D2\u4E0D\u64A4\u9500\u7684\uFF0C\u4E0A\u7EA7\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u53EF\u4EE5\u76F4\u63A5\u64A4\u9500\uFF0C\u5E76\u5BF9\u4F5C\u51FA\u8BE5\u5BA1\u6279\u51B3\u5B9A\u7684\u76F4\u63A5\u8D1F\u8D23\u7684\u4E3B\u7BA1\u4EBA\u5458\u548C\u5176\u4ED6\u76F4\u63A5\u8D23\u4EFB\u4EBA\u5458\uFF0C\u79FB\u4EA4\u7EAA\u68C0\u76D1\u5BDF\u673A\u5173\u548C\u7EC4\u7EC7\uFF08\u4EBA\u4E8B\uFF09\u90E8\u95E8\uFF0C\u7531\u7EAA\u68C0\u76D1\u5BDF\u673A\u5173\u548C\u7EC4\u7EC7\uFF08\u4EBA\u4E8B\uFF09\u90E8\u95E8\u4F9D\u6CD5\u4F9D\u89C4\u8FFD\u7A76\u76F8\u5173\u8D23\u4EFB\u3002</p><p id="t18">\u2003\u2003<b>\u7B2C\u5341\u516B\u6761</b>\u2003\u5B9E\u65BD\u533A\u57DF\u9650\u6279\u671F\u95F4\uFF0C\u88AB\u9650\u6279\u5730\u533A\u672A\u4F9D\u6CD5\u5F00\u5C55\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u7684\u5EFA\u8BBE\u9879\u76EE\u64C5\u81EA\u5F00\u5DE5\u5EFA\u8BBE\u7684\uFF0C\u7531\u8D1F\u6709\u73AF\u5883\u4FDD\u62A4\u76D1\u7763\u7BA1\u7406\u804C\u8D23\u7684\u90E8\u95E8\u4F9D\u6CD5\u8D23\u4EE4\u5EFA\u8BBE\u5355\u4F4D\u505C\u6B62\u5EFA\u8BBE\uFF0C\u5904\u4EE5\u7F5A\u6B3E\uFF0C\u5E76\u8D23\u4EE4\u6062\u590D\u539F\u72B6\u3002</p><p id="t19">\u2003\u2003<b>\u7B2C\u5341\u4E5D\u6761</b>\u2003\u5BF9\u5E72\u9884\u9650\u6279\u51B3\u5B9A\u5B9E\u65BD\u3001\u5305\u5E87\u7EB5\u5BB9\u73AF\u5883\u8FDD\u6CD5\u884C\u4E3A\u3001\u5C65\u804C\u4E0D\u529B\u3001\u76D1\u7BA1\u4E0D\u4E25\u7684\u5730\u65B9\u4EBA\u6C11\u653F\u5E9C\u3001\u5730\u65B9\u73AF\u5883\u4FDD\u62A4\u90E8\u95E8\u7684\u76F8\u5173\u8D23\u4EFB\u4EBA\u5458\uFF0C\u73AF\u5883\u4FDD\u62A4\u90E8\u5C06\u76F8\u5173\u6750\u6599\u79FB\u4EA4\u7EAA\u68C0\u76D1\u5BDF\u673A\u5173\u548C\u7EC4\u7EC7\uFF08\u4EBA\u4E8B\uFF09\u90E8\u95E8\uFF0C\u7531\u7EAA\u68C0\u76D1\u5BDF\u673A\u5173\u548C\u7EC4\u7EC7(\u4EBA\u4E8B)\u90E8\u95E8\u4F9D\u6CD5\u4F9D\u89C4\u8FFD\u7A76\u76F8\u5173\u8D23\u4EFB\u3002</p><p id="t20">\u2003\u2003<b>\u7B2C\u4E8C\u5341\u6761</b>\u2003\u672C\u529E\u6CD5\u7531\u73AF\u5883\u4FDD\u62A4\u90E8\u8D1F\u8D23\u89E3\u91CA\u3002</p><p id="t21">\u2003\u2003<b>\u7B2C\u4E8C\u5341\u4E00\u6761</b>\u2003\u672C\u529E\u6CD5\u81EA2016\u5E741\u67081\u65E5\u8D77\u65BD\u884C\u3002</p><hr>',23),h={class:"custom-container warning"},c=t("p",{class:"custom-container-title"},"\u539F\u6587\u94FE\u63A5",-1),f={href:"http://www.mee.gov.cn/gkml/hbb/bwj/201602/t20160223_330537.htm",target:"_blank",rel:"noopener noreferrer"},l=o("http://www.mee.gov.cn/gkml/hbb/bwj/201602/t20160223_330537.htm");function _(m,u){const r=s("ExternalLinkIcon");return e(),a("div",null,[d,t("div",h,[c,t("p",null,[t("a",f,[l,p(r)])])])])}const g=b(n,[["render",_],["__file","jsxmhjyxpjqyxpglbf.html.vue"]]);export{g as default};
