import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as b,c as a,a as t,b as p,e as i,d as s,r as n}from"./app.a9f6469a.js";const o={},c=i('<div class="custom-container tip"><p class="custom-container-title">\u4FEE\u8BA2\u4FE1\u606F</p><p>\uFF082011\u5E744\u670818\u65E5\u73AF\u5883\u4FDD\u62A4\u90E8\u4EE4\u7B2C17\u53F7\u516C\u5E03\u2003\u81EA2011\u5E745\u67081\u65E5\u8D77\u65BD\u884C\uFF09</p></div><details class="custom-container details"><summary>\u76EE\u5F55</summary><ul><li><p>\u7B2C1~17\u6761\uFF1A</p><p><a href="#t1">1</a>\u2003<a href="#t2">2</a>\u2003<a href="#t3">3</a>\u2003<a href="#t4">4</a>\u2003<a href="#t5">5</a>\u2003<a href="#t6">6</a>\u2003<a href="#t7">7</a>\u2003<a href="#t8">8</a>\u2003<a href="#t9">9</a>\u2003<a href="#t10">10</a>\u2003<a href="#t11">11</a>\u2003<a href="#t12">12</a>\u2003<a href="#t13">13</a>\u2003<a href="#t14">14</a>\u2003<a href="#t15">15</a>\u2003<a href="#t16">16</a>\u2003<a href="#t17">17</a></p></li></ul></details><p id="t1">\u2003\u2003<b>\u7B2C\u4E00\u6761</b>\u2003\u4E3A\u4E86\u89C4\u8303\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u62A5\u544A\u5DE5\u4F5C\uFF0C\u63D0\u9AD8\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5BF9\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u80FD\u529B\uFF0C\u4F9D\u636E\u300A\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u7A81\u53D1\u4E8B\u4EF6\u5E94\u5BF9\u6CD5\u300B\u3001\u300A\u56FD\u5BB6\u7A81\u53D1\u516C\u5171\u4E8B\u4EF6\u603B\u4F53\u5E94\u6025\u9884\u6848\u300B\u3001\u300A\u56FD\u5BB6\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5E94\u6025\u9884\u6848\u300B\u53CA\u76F8\u5173\u6CD5\u5F8B\u6CD5\u89C4\u7684\u89C4\u5B9A\uFF0C\u5236\u5B9A\u672C\u529E\u6CD5\u3002</p><p id="t2">\u2003\u2003<b>\u7B2C\u4E8C\u6761</b>\u2003\u672C\u529E\u6CD5\u9002\u7528\u4E8E\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5BF9\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u4FE1\u606F\u62A5\u544A\u3002<br> \u2003\u2003\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5206\u4E3A\u7279\u522B\u91CD\u5927\uFF08\u2160\u7EA7\uFF09\u3001\u91CD\u5927\uFF08\u2161\u7EA7\uFF09\u3001\u8F83\u5927\uFF08\u2162\u7EA7\uFF09\u548C\u4E00\u822C\uFF08\u2163\u7EA7\uFF09\u56DB\u7EA7\u3002<br> \u2003\u2003\u6838\u4E0E\u8F90\u5C04\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u4FE1\u606F\u62A5\u544A\u6309\u7167\u6838\u5B89\u5168\u6709\u5173\u6CD5\u5F8B\u6CD5\u89C4\u6267\u884C\u3002</p><p id="t3">\u2003\u2003<b>\u7B2C\u4E09\u6761</b>\u2003\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u53D1\u751F\u5730\u8BBE\u533A\u7684\u5E02\u7EA7\u6216\u8005\u53BF\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5728\u53D1\u73B0\u6216\u8005\u5F97\u77E5\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u540E\uFF0C\u5E94\u5F53\u7ACB\u5373\u8FDB\u884C\u6838\u5B9E\uFF0C\u5BF9\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u6027\u8D28\u548C\u7C7B\u522B\u505A\u51FA\u521D\u6B65\u8BA4\u5B9A\u3002<br> \u2003\u2003\u5BF9\u521D\u6B65\u8BA4\u5B9A\u4E3A\u4E00\u822C\uFF08IV\u7EA7\uFF09\u6216\u8005\u8F83\u5927\uFF08III\u7EA7\uFF09\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\uFF0C\u4E8B\u4EF6\u53D1\u751F\u5730\u8BBE\u533A\u7684\u5E02\u7EA7\u6216\u8005\u53BF\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u5728\u56DB\u5C0F\u65F6\u5185\u5411\u672C\u7EA7\u4EBA\u6C11\u653F\u5E9C\u548C\u4E0A\u4E00\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u62A5\u544A\u3002<br> \u2003\u2003\u5BF9\u521D\u6B65\u8BA4\u5B9A\u4E3A\u91CD\u5927\uFF08II\u7EA7\uFF09\u6216\u8005\u7279\u522B\u91CD\u5927\uFF08I\u7EA7\uFF09\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\uFF0C\u4E8B\u4EF6\u53D1\u751F\u5730\u8BBE\u533A\u7684\u5E02\u7EA7\u6216\u8005\u53BF\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u5728\u4E24\u5C0F\u65F6\u5185\u5411\u672C\u7EA7\u4EBA\u6C11\u653F\u5E9C\u548C\u7701\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u62A5\u544A\uFF0C\u540C\u65F6\u4E0A\u62A5\u73AF\u5883\u4FDD\u62A4\u90E8\u3002\u7701\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u63A5\u5230\u62A5\u544A\u540E\uFF0C\u5E94\u5F53\u8FDB\u884C\u6838\u5B9E\u5E76\u5728\u4E00\u5C0F\u65F6\u5185\u62A5\u544A\u73AF\u5883\u4FDD\u62A4\u90E8\u3002<br> \u2003\u2003\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5904\u7F6E\u8FC7\u7A0B\u4E2D\u4E8B\u4EF6\u7EA7\u522B\u53D1\u751F\u53D8\u5316\u7684\uFF0C\u5E94\u5F53\u6309\u7167\u53D8\u5316\u540E\u7684\u7EA7\u522B\u62A5\u544A\u4FE1\u606F\u3002</p><p id="t4">\u2003\u2003<b>\u7B2C\u56DB\u6761</b>\u2003\u53D1\u751F\u4E0B\u5217\u4E00\u65F6\u65E0\u6CD5\u5224\u660E\u7B49\u7EA7\u7684\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\uFF0C\u4E8B\u4EF6\u53D1\u751F\u5730\u8BBE\u533A\u7684\u5E02\u7EA7\u6216\u8005\u53BF\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u6309\u7167\u91CD\u5927\uFF08II\u7EA7\uFF09\u6216\u8005\u7279\u522B\u91CD\u5927\uFF08I\u7EA7\uFF09\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u62A5\u544A\u7A0B\u5E8F\u4E0A\u62A5\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u5BF9\u996E\u7528\u6C34\u6C34\u6E90\u4FDD\u62A4\u533A\u9020\u6210\u6216\u8005\u53EF\u80FD\u9020\u6210\u5F71\u54CD\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u6D89\u53CA\u5C45\u6C11\u805A\u5C45\u533A\u3001\u5B66\u6821\u3001\u533B\u9662\u7B49\u654F\u611F\u533A\u57DF\u548C\u654F\u611F\u4EBA\u7FA4\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u6D89\u53CA\u91CD\u91D1\u5C5E\u6216\u8005\u7C7B\u91D1\u5C5E\u6C61\u67D3\u7684\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09\u6709\u53EF\u80FD\u4EA7\u751F\u8DE8\u7701\u6216\u8005\u8DE8\u56FD\u5F71\u54CD\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E94\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u5F15\u53D1\u7FA4\u4F53\u6027\u4E8B\u4EF6\uFF0C\u6216\u8005\u793E\u4F1A\u5F71\u54CD\u8F83\u5927\u7684\uFF1B<br> \u2003\u2003\uFF08\u516D\uFF09\u5730\u65B9\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u8BA4\u4E3A\u6709\u5FC5\u8981\u62A5\u544A\u7684\u5176\u4ED6\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002</p><p id="t5">\u2003\u2003<b>\u7B2C\u4E94\u6761</b>\u2003\u4E0A\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5148\u4E8E\u4E0B\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u83B7\u6089\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u7684\uFF0C\u53EF\u4EE5\u8981\u6C42\u4E0B\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u6838\u5B9E\u5E76\u62A5\u544A\u76F8\u5E94\u4FE1\u606F\u3002\u4E0B\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u4F9D\u7167\u672C\u529E\u6CD5\u7684\u89C4\u5B9A\u62A5\u544A\u4FE1\u606F\u3002</p><p id="t6">\u2003\u2003<b>\u7B2C\u516D\u6761</b>\u2003\u5411\u73AF\u5883\u4FDD\u62A4\u90E8\u62A5\u544A\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u6709\u5173\u4FE1\u606F\u7684\uFF0C\u5E94\u5F53\u62A5\u544A\u603B\u503C\u73ED\u5BA4\uFF0C\u540C\u65F6\u62A5\u544A\u73AF\u5883\u4FDD\u62A4\u90E8\u73AF\u5883\u5E94\u6025\u6307\u6325\u9886\u5BFC\u5C0F\u7EC4\u529E\u516C\u5BA4\u3002\u73AF\u5883\u4FDD\u62A4\u90E8\u73AF\u5883\u5E94\u6025\u6307\u6325\u9886\u5BFC\u5C0F\u7EC4\u529E\u516C\u5BA4\u5E94\u5F53\u6839\u636E\u60C5\u51B5\u5411\u90E8\u5185\u76F8\u5173\u53F8\u5C40\u901A\u62A5\u6709\u5173\u4FE1\u606F\u3002</p><p id="t7">\u2003\u2003<b>\u7B2C\u4E03\u6761</b>\u2003\u73AF\u5883\u4FDD\u62A4\u90E8\u5728\u63A5\u5230\u4E0B\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u91CD\u5927\uFF08II\u7EA7\uFF09\u6216\u8005\u7279\u522B\u91CD\u5927\uFF08I\u7EA7\uFF09\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4EE5\u53CA\u5176\u4ED6\u6709\u5FC5\u8981\u62A5\u544A\u7684\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u540E\uFF0C\u5E94\u5F53\u53CA\u65F6\u5411\u56FD\u52A1\u9662\u603B\u503C\u73ED\u5BA4\u548C\u4E2D\u5171\u4E2D\u592E\u529E\u516C\u5385\u79D8\u4E66\u5C40\u62A5\u544A\u3002</p><p id="t8">\u2003\u2003<b>\u7B2C\u516B\u6761</b>\u2003\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5DF2\u7ECF\u6216\u8005\u53EF\u80FD\u6D89\u53CA\u76F8\u90BB\u884C\u653F\u533A\u57DF\u7684\uFF0C\u4E8B\u4EF6\u53D1\u751F\u5730\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u53CA\u65F6\u901A\u62A5\u76F8\u90BB\u533A\u57DF\u540C\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\uFF0C\u5E76\u5411\u672C\u7EA7\u4EBA\u6C11\u653F\u5E9C\u63D0\u51FA\u5411\u76F8\u90BB\u533A\u57DF\u4EBA\u6C11\u653F\u5E9C\u901A\u62A5\u7684\u5EFA\u8BAE\u3002\u63A5\u5230\u901A\u62A5\u7684\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u53CA\u65F6\u8C03\u67E5\u4E86\u89E3\u60C5\u51B5\uFF0C\u5E76\u6309\u7167\u672C\u529E\u6CD5\u7B2C\u4E09\u6761\u3001\u7B2C\u56DB\u6761\u7684\u89C4\u5B9A\u62A5\u544A\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u3002</p><p id="t9">\u2003\u2003<b>\u7B2C\u4E5D\u6761</b>\u2003\u4E0A\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u63A5\u5230\u4E0B\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u4EE5\u7535\u8BDD\u5F62\u5F0F\u62A5\u544A\u7684\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u540E\uFF0C\u5E94\u5F53\u5982\u5B9E\u3001\u51C6\u786E\u505A\u597D\u8BB0\u5F55\uFF0C\u5E76\u8981\u6C42\u4E0B\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u53CA\u65F6\u62A5\u544A\u4E66\u9762\u4FE1\u606F\u3002<br> \u2003\u2003\u5BF9\u4E8E\u60C5\u51B5\u4E0D\u591F\u6E05\u695A\u3001\u8981\u7D20\u4E0D\u5168\u7684\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\uFF0C\u4E0A\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u8981\u6C42\u4E0B\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u53CA\u65F6\u6838\u5B9E\u8865\u5145\u4FE1\u606F\u3002</p><p id="t10">\u2003\u2003<b>\u7B2C\u5341\u6761</b>\u2003\u53BF\u7EA7\u4EE5\u4E0A\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5E94\u5F53\u5EFA\u7ACB\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u6863\u6848\uFF0C\u5E76\u6309\u7167\u6709\u5173\u89C4\u5B9A\u5411\u4E0A\u4E00\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u62A5\u9001\u672C\u884C\u653F\u533A\u57DF\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u6708\u5EA6\u3001\u5B63\u5EA6\u3001\u534A\u5E74\u5EA6\u548C\u5E74\u5EA6\u62A5\u544A\u4EE5\u53CA\u7EDF\u8BA1\u60C5\u51B5\u3002\u4E0A\u4E00\u7EA7\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u5B9A\u671F\u5BF9\u62A5\u544A\u53CA\u7EDF\u8BA1\u60C5\u51B5\u8FDB\u884C\u901A\u62A5\u3002</p><p id="t11">\u2003\u2003<b>\u7B2C\u5341\u4E00\u6761</b>\u2003\u62A5\u544A\u6D89\u53CA\u56FD\u5BB6\u79D8\u5BC6\u7684\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\uFF0C\u5E94\u5F53\u9075\u5B88\u56FD\u5BB6\u6709\u5173\u4FDD\u5BC6\u7684\u89C4\u5B9A\u3002</p><p id="t12">\u2003\u2003<b>\u7B2C\u5341\u4E8C\u6761</b>\u2003\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u62A5\u544A\u5206\u4E3A\u521D\u62A5\u3001\u7EED\u62A5\u548C\u5904\u7406\u7ED3\u679C\u62A5\u544A\u3002<br> \u2003\u2003\u521D\u62A5\u5728\u53D1\u73B0\u6216\u8005\u5F97\u77E5\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u540E\u9996\u6B21\u4E0A\u62A5\uFF1B\u7EED\u62A5\u5728\u67E5\u6E05\u6709\u5173\u57FA\u672C\u60C5\u51B5\u3001\u4E8B\u4EF6\u53D1\u5C55\u60C5\u51B5\u540E\u968F\u65F6\u4E0A\u62A5\uFF1B\u5904\u7406\u7ED3\u679C\u62A5\u544A\u5728\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5904\u7406\u5B8C\u6BD5\u540E\u4E0A\u62A5\u3002</p><p id="t13">\u2003\u2003<b>\u7B2C\u5341\u4E09\u6761</b>\u2003\u521D\u62A5\u5E94\u5F53\u62A5\u544A\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u53D1\u751F\u65F6\u95F4\u3001\u5730\u70B9\u3001\u4FE1\u606F\u6765\u6E90\u3001\u4E8B\u4EF6\u8D77\u56E0\u548C\u6027\u8D28\u3001\u57FA\u672C\u8FC7\u7A0B\u3001\u4E3B\u8981\u6C61\u67D3\u7269\u548C\u6570\u91CF\u3001\u76D1\u6D4B\u6570\u636E\u3001\u4EBA\u5458\u53D7\u5BB3\u60C5\u51B5\u3001\u996E\u7528\u6C34\u6C34\u6E90\u5730\u7B49\u73AF\u5883\u654F\u611F\u70B9\u53D7\u5F71\u54CD\u60C5\u51B5\u3001\u4E8B\u4EF6\u53D1\u5C55\u8D8B\u52BF\u3001\u5904\u7F6E\u60C5\u51B5\u3001\u62DF\u91C7\u53D6\u7684\u63AA\u65BD\u4EE5\u53CA\u4E0B\u4E00\u6B65\u5DE5\u4F5C\u5EFA\u8BAE\u7B49\u521D\u6B65\u60C5\u51B5\uFF0C\u5E76\u63D0\u4F9B\u53EF\u80FD\u53D7\u5230\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5F71\u54CD\u7684\u73AF\u5883\u654F\u611F\u70B9\u7684\u5206\u5E03\u793A\u610F\u56FE\u3002<br> \u2003\u2003\u7EED\u62A5\u5E94\u5F53\u5728\u521D\u62A5\u7684\u57FA\u7840\u4E0A\uFF0C\u62A5\u544A\u6709\u5173\u5904\u7F6E\u8FDB\u5C55\u60C5\u51B5\u3002<br> \u2003\u2003\u5904\u7406\u7ED3\u679C\u62A5\u544A\u5E94\u5F53\u5728\u521D\u62A5\u548C\u7EED\u62A5\u7684\u57FA\u7840\u4E0A\uFF0C\u62A5\u544A\u5904\u7406\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u63AA\u65BD\u3001\u8FC7\u7A0B\u548C\u7ED3\u679C\uFF0C\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u6F5C\u5728\u6216\u8005\u95F4\u63A5\u5371\u5BB3\u4EE5\u53CA\u635F\u5931\u3001\u793E\u4F1A\u5F71\u54CD\u3001\u5904\u7406\u540E\u7684\u9057\u7559\u95EE\u9898\u3001\u8D23\u4EFB\u8FFD\u7A76\u7B49\u8BE6\u7EC6\u60C5\u51B5\u3002</p><p id="t14">\u2003\u2003<b>\u7B2C\u5341\u56DB\u6761</b>\u2003\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u5E94\u5F53\u91C7\u7528\u4F20\u771F\u3001\u7F51\u7EDC\u3001\u90AE\u5BC4\u548C\u9762\u5448\u7B49\u65B9\u5F0F\u4E66\u9762\u62A5\u544A\uFF1B\u60C5\u51B5\u7D27\u6025\u65F6\uFF0C\u521D\u62A5\u53EF\u901A\u8FC7\u7535\u8BDD\u62A5\u544A\uFF0C\u4F46\u5E94\u5F53\u53CA\u65F6\u8865\u5145\u4E66\u9762\u62A5\u544A\u3002<br> \u2003\u2003\u4E66\u9762\u62A5\u544A\u4E2D\u5E94\u5F53\u8F7D\u660E\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u62A5\u544A\u5355\u4F4D\u3001\u62A5\u544A\u7B7E\u53D1\u4EBA\u3001\u8054\u7CFB\u4EBA\u53CA\u8054\u7CFB\u65B9\u5F0F\u7B49\u5185\u5BB9\uFF0C\u5E76\u5C3D\u53EF\u80FD\u63D0\u4F9B\u5730\u56FE\u3001\u56FE\u7247\u4EE5\u53CA\u76F8\u5173\u7684\u591A\u5A92\u4F53\u8D44\u6599\u3002</p><p id="t15">\u2003\u2003<b>\u7B2C\u5341\u4E94\u6761</b>\u2003\u5728\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u62A5\u544A\u5DE5\u4F5C\u4E2D\u8FDF\u62A5\u3001\u8C0E\u62A5\u3001\u7792\u62A5\u3001\u6F0F\u62A5\u6709\u5173\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u7684\uFF0C\u7ED9\u4E88\u901A\u62A5\u6279\u8BC4\uFF1B\u9020\u6210\u540E\u679C\u7684\uFF0C\u5BF9\u76F4\u63A5\u8D1F\u8D23\u7684\u4E3B\u7BA1\u4EBA\u5458\u548C\u5176\u4ED6\u76F4\u63A5\u8D23\u4EFB\u4EBA\u5458\u4F9D\u6CD5\u4F9D\u7EAA\u7ED9\u4E88\u5904\u5206\uFF1B\u6784\u6210\u72AF\u7F6A\u7684\uFF0C\u79FB\u9001\u53F8\u6CD5\u673A\u5173\u4F9D\u6CD5\u8FFD\u7A76\u5211\u4E8B\u8D23\u4EFB\u3002</p><p id="t16">\u2003\u2003<b>\u7B2C\u5341\u516D\u6761</b>\u2003\u672C\u529E\u6CD5\u7531\u73AF\u5883\u4FDD\u62A4\u90E8\u89E3\u91CA\u3002</p><p id="t17">\u2003\u2003<b>\u7B2C\u5341\u4E03\u6761</b>\u2003\u672C\u529E\u6CD5\u81EA2011\u5E745\u67081\u65E5\u8D77\u65BD\u884C\u3002\u300A\u73AF\u5883\u4FDD\u62A4\u884C\u653F\u4E3B\u7BA1\u90E8\u95E8\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4FE1\u606F\u62A5\u544A\u529E\u6CD5\uFF08\u8BD5\u884C\uFF09\u300B\uFF08\u73AF\u53D1\u30142006\u301550\u53F7\uFF09\u540C\u65F6\u5E9F\u6B62\u3002</p><hr><div class="custom-container info"><p class="custom-container-title">\u9644\u5F55</p><h2 align="center" id="\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5206\u7EA7\u6807\u51C6" tabindex="-1"><a class="header-anchor" href="#\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5206\u7EA7\u6807\u51C6" aria-hidden="true">#</a> \u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5206\u7EA7\u6807\u51C6</h2><p>\u2003\u2003\u6309\u7167\u7A81\u53D1\u4E8B\u4EF6\u4E25\u91CD\u6027\u548C\u7D27\u6025\u7A0B\u5EA6\uFF0C\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5206\u4E3A\u7279\u522B\u91CD\u5927\uFF08\u2160\u7EA7\uFF09\u3001\u91CD\u5927\uFF08\u2161\u7EA7\uFF09\u3001\u8F83\u5927\uFF08\u2162\u7EA7\uFF09\u548C\u4E00\u822C\uFF08\u2163\u7EA7\uFF09\u56DB\u7EA7\u3002</p><p>\u2003\u20031\u3001<b>\u7279\u522B\u91CD\u5927\uFF08\u2160\u7EA7\uFF09</b>\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002<br> \u2003\u2003\u51E1\u7B26\u5408\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u4E3A\u7279\u522B\u91CD\u5927\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\uFF1A<br> \u2003\u2003\uFF081\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u76F4\u63A5\u5BFC\u81F410\u4EBA\u4EE5\u4E0A\u6B7B\u4EA1\u6216100\u4EBA\u4EE5\u4E0A\u4E2D\u6BD2\u7684\uFF1B<br> \u2003\u2003\uFF082\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9700\u758F\u6563\u3001\u8F6C\u79FB\u7FA4\u4F175\u4E07\u4EBA\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF083\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u76F4\u63A5\u7ECF\u6D4E\u635F\u59311\u4EBF\u5143\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF084\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u533A\u57DF\u751F\u6001\u529F\u80FD\u4E27\u5931\u6216\u56FD\u5BB6\u91CD\u70B9\u4FDD\u62A4\u7269\u79CD\u706D\u7EDD\u7684\uFF1B<br> \u2003\u2003\uFF085\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u5730\u5E02\u7EA7\u4EE5\u4E0A\u57CE\u5E02\u96C6\u4E2D\u5F0F\u996E\u7528\u6C34\u6C34\u6E90\u5730\u53D6\u6C34\u4E2D\u65AD\u7684\uFF1B<br> \u2003\u2003\uFF086\uFF091\u30012\u7C7B\u653E\u5C04\u6E90\u5931\u63A7\u9020\u6210\u5927\u8303\u56F4\u4E25\u91CD\u8F90\u5C04\u6C61\u67D3\u540E\u679C\u7684\uFF1B\u6838\u8BBE\u65BD\u53D1\u751F\u9700\u8981\u8FDB\u5165\u573A\u5916\u5E94\u6025\u7684\u4E25\u91CD\u6838\u4E8B\u6545\uFF0C\u6216\u4E8B\u6545\u8F90\u5C04\u540E\u679C\u53EF\u80FD\u5F71\u54CD\u90BB\u7701\u548C\u5883\u5916\u7684\uFF0C\u6216\u6309\u7167\u201C\u56FD\u9645\u6838\u4E8B\u4EF6\u5206\u7EA7\uFF08INES\uFF09\u6807\u51C6\u201D\u5C5E\u4E8E3\u7EA7\u4EE5\u4E0A\u7684\u6838\u4E8B\u4EF6\uFF1B\u53F0\u6E7E\u6838\u8BBE\u65BD\u4E2D\u53D1\u751F\u7684\u6309\u7167\u201C\u56FD\u9645\u6838\u4E8B\u4EF6\u5206\u7EA7\uFF08INES\uFF09\u6807\u51C6\u201D\u5C5E\u4E8E4\u7EA7\u4EE5\u4E0A\u7684\u6838\u4E8B\u6545\uFF1B\u5468\u8FB9\u56FD\u5BB6\u6838\u8BBE\u65BD\u4E2D\u53D1\u751F\u7684\u6309\u7167\u201C\u56FD\u9645\u6838\u4E8B\u4EF6\u5206\u7EA7\uFF08INES\uFF09\u6807\u51C6\u201D\u5C5E\u4E8E4\u7EA7\u4EE5\u4E0A\u7684\u6838\u4E8B\u6545\uFF1B<br> \u2003\u2003\uFF087\uFF09\u8DE8\u56FD\u754C\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002</p><p>\u2003\u20032\u3001<b>\u91CD\u5927\uFF08\u2161\u7EA7\uFF09</b>\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002<br> \u2003\u2003\u51E1\u7B26\u5408\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u4E3A\u91CD\u5927\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\uFF1A<br> \u2003\u2003\uFF081\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u76F4\u63A5\u5BFC\u81F43\u4EBA\u4EE5\u4E0A10\u4EBA\u4EE5\u4E0B\u6B7B\u4EA1\u621650\u4EBA\u4EE5\u4E0A100\u4EBA\u4EE5\u4E0B\u4E2D\u6BD2\u7684\uFF1B<br> \u2003\u2003\uFF082\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9700\u758F\u6563\u3001\u8F6C\u79FB\u7FA4\u4F171\u4E07\u4EBA\u4EE5\u4E0A5\u4E07\u4EBA\u4EE5\u4E0B\u7684\uFF1B<br> \u2003\u2003\uFF083\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u76F4\u63A5\u7ECF\u6D4E\u635F\u59312000\u4E07\u5143\u4EE5\u4E0A1\u4EBF\u5143\u4EE5\u4E0B\u7684\uFF1B<br> \u2003\u2003\uFF084\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u533A\u57DF\u751F\u6001\u529F\u80FD\u90E8\u5206\u4E27\u5931\u6216\u56FD\u5BB6\u91CD\u70B9\u4FDD\u62A4\u91CE\u751F\u52A8\u690D\u7269\u79CD\u7FA4\u5927\u6279\u6B7B\u4EA1\u7684\uFF1B<br> \u2003\u2003\uFF085\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u53BF\u7EA7\u57CE\u5E02\u96C6\u4E2D\u5F0F\u996E\u7528\u6C34\u6C34\u6E90\u5730\u53D6\u6C34\u4E2D\u65AD\u7684\uFF1B<br> \u2003\u2003\uFF086\uFF09\u91CD\u91D1\u5C5E\u6C61\u67D3\u6216\u5371\u9669\u5316\u5B66\u54C1\u751F\u4EA7\u3001\u8D2E\u8FD0\u3001\u4F7F\u7528\u8FC7\u7A0B\u4E2D\u53D1\u751F\u7206\u70B8\u3001\u6CC4\u6F0F\u7B49\u4E8B\u4EF6\uFF0C\u6216\u56E0\u503E\u5012\u3001\u5806\u653E\u3001\u4E22\u5F03\u3001\u9057\u6492\u5371\u9669\u5E9F\u7269\u7B49\u9020\u6210\u7684\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u53D1\u751F\u5728\u56FD\u5BB6\u91CD\u70B9\u6D41\u57DF\u3001\u56FD\u5BB6\u7EA7\u81EA\u7136\u4FDD\u62A4\u533A\u3001\u98CE\u666F\u540D\u80DC\u533A\u6216\u5C45\u6C11\u805A\u96C6\u533A\u3001\u533B\u9662\u3001\u5B66\u6821\u7B49\u654F\u611F\u533A\u57DF\u7684\uFF1B<br> \u2003\u2003\uFF087\uFF091\u30012\u7C7B\u653E\u5C04\u6E90\u4E22\u5931\u3001\u88AB\u76D7\u3001\u5931\u63A7\u9020\u6210\u73AF\u5883\u5F71\u54CD\uFF0C\u6216\u6838\u8BBE\u65BD\u548C\u94C0\u77FF\u51B6\u70BC\u8BBE\u65BD\u53D1\u751F\u7684\u8FBE\u5230\u8FDB\u5165\u573A\u533A\u5E94\u6025\u72B6\u6001\u6807\u51C6\u7684\uFF0C\u6216\u8FDB\u53E3\u8D27\u7269\u4E25\u91CD\u8F90\u5C04\u8D85\u6807\u7684\u4E8B\u4EF6\uFF1B<br> \u2003\u2003\uFF088\uFF09\u8DE8\u7701\uFF08\u533A\u3001\u5E02\uFF09\u754C\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002</p><p>\u2003\u20033\u3001<b>\u8F83\u5927\uFF08\u2162\u7EA7\uFF09</b>\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002<br> \u2003\u2003\u51E1\u7B26\u5408\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u4E3A\u8F83\u5927\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\uFF1A<br> \u2003\u2003\uFF081\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u76F4\u63A5\u5BFC\u81F43\u4EBA\u4EE5\u4E0B\u6B7B\u4EA1\u621610\u4EBA\u4EE5\u4E0A50\u4EBA\u4EE5\u4E0B\u4E2D\u6BD2\u7684\uFF1B<br> \u2003\u2003\uFF082\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9700\u758F\u6563\u3001\u8F6C\u79FB\u7FA4\u4F175000\u4EBA\u4EE5\u4E0A1\u4E07\u4EBA\u4EE5\u4E0B\u7684\uFF1B<br> \u2003\u2003\uFF083\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u76F4\u63A5\u7ECF\u6D4E\u635F\u5931500\u4E07\u5143\u4EE5\u4E0A2000\u4E07\u5143\u4EE5\u4E0B\u7684\uFF1B<br> \u2003\u2003\uFF084\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u56FD\u5BB6\u91CD\u70B9\u4FDD\u62A4\u7684\u52A8\u690D\u7269\u7269\u79CD\u53D7\u5230\u7834\u574F\u7684\uFF1B<br> \u2003\u2003\uFF085\uFF09\u56E0\u73AF\u5883\u6C61\u67D3\u9020\u6210\u4E61\u9547\u96C6\u4E2D\u5F0F\u996E\u7528\u6C34\u6C34\u6E90\u5730\u53D6\u6C34\u4E2D\u65AD\u7684\uFF1B<br> \u2003\u2003\uFF086\uFF093\u7C7B\u653E\u5C04\u6E90\u4E22\u5931\u3001\u88AB\u76D7\u6216\u5931\u63A7\uFF0C\u9020\u6210\u73AF\u5883\u5F71\u54CD\u7684\uFF1B<br> \u2003\u2003\uFF087\uFF09\u8DE8\u5730\u5E02\u754C\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002</p><p>\u2003\u20034\u3001<b>\u4E00\u822C\uFF08\u2163\u7EA7\uFF09</b>\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002<br> \u2003\u2003\u9664\u7279\u522B\u91CD\u5927\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3001\u91CD\u5927\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3001\u8F83\u5927\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u4EE5\u5916\u7684\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u3002</p></div><hr>',22),d={class:"custom-container warning"},h=t("p",{class:"custom-container-title"},"\u539F\u6587\u94FE\u63A5",-1),l={href:"http://www.mee.gov.cn/gzk/gz/202112/t20211210_963741.shtml",target:"_blank",rel:"noopener noreferrer"},f=s("http://www.mee.gov.cn/gzk/gz/202112/t20211210_963741.shtml");function _(m,u){const r=n("ExternalLinkIcon");return b(),a("div",null,[c,t("div",d,[h,t("p",null,[t("a",l,[f,p(r)])])])])}const x=e(o,[["render",_],["__file","tfhjsjxxbgbf.html.vue"]]);export{x as default};
