import{_ as b}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as a,c as e,a as r,b as p,e as i,d as s,r as n}from"./app.8e263a3f.js";const o={},c=i('<div class="custom-container tip"><p class="custom-container-title">\u4FEE\u8BA2\u4FE1\u606F</p><p style="text-align:center;">\u6CD5\u91CA\u30142016\u301529\u53F7</p><p>(2016\u5E7411\u67087\u65E5\u6700\u9AD8\u4EBA\u6C11\u6CD5\u9662\u5BA1\u5224\u59D4\u5458\u4F1A\u7B2C1698\u6B21\u4F1A\u8BAE\u30012016\u5E7412\u67088\u65E5\u6700\u9AD8\u4EBA\u6C11\u68C0\u5BDF\u9662\u7B2C\u5341\u4E8C\u5C4A\u68C0\u5BDF\u59D4\u5458\u4F1A\u7B2C58\u6B21\u4F1A\u8BAE\u901A\u8FC7\uFF0C\u81EA2017\u5E741\u67081\u65E5\u8D77\u65BD\u884C\uFF09</p></div><p>\u2003\u2003\u4E3A\u4F9D\u6CD5\u60E9\u6CBB\u6709\u5173\u73AF\u5883\u6C61\u67D3\u72AF\u7F6A\uFF0C\u6839\u636E\u300A\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u5211\u6CD5\u300B\u300A\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u5211\u4E8B\u8BC9\u8BBC\u6CD5\u300B\u7684\u6709\u5173\u89C4\u5B9A\uFF0C\u73B0\u5C31\u529E\u7406\u6B64\u7C7B\u5211\u4E8B\u6848\u4EF6\u9002\u7528\u6CD5\u5F8B\u7684\u82E5\u5E72\u95EE\u9898\u89E3\u91CA\u5982\u4E0B\uFF1A</p><details class="custom-container details"><summary>\u76EE\u5F55</summary><ul><li><p>\u7B2C1~18\u6761\uFF1A</p><p><a href="#t1">1</a>\u2003<a href="#t2">2</a>\u2003<a href="#t3">3</a>\u2003<a href="#t4">4</a>\u2003<a href="#t5">5</a>\u2003<a href="#t6">6</a>\u2003<a href="#t7">7</a>\u2003<a href="#t8">8</a>\u2003<a href="#t9">9</a>\u2003<a href="#t10">10</a>\u2003<a href="#t11">11</a>\u2003<a href="#t12">12</a>\u2003<a href="#t13">13</a>\u2003<a href="#t14">14</a>\u2003<a href="#t15">15</a>\u2003<a href="#t16">16</a>\u2003<a href="#t17">17</a>\u2003<a href="#t18">18</a></p></li></ul></details><p id="t1">\u2003\u2003<b>\u7B2C\u4E00\u6761</b>\u2003\u5B9E\u65BD\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u89C4\u5B9A\u7684\u884C\u4E3A\uFF0C\u5177\u6709\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u5E94\u5F53\u8BA4\u5B9A\u4E3A\u201C\u4E25\u91CD\u6C61\u67D3\u73AF\u5883\u201D\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u5728\u996E\u7528\u6C34\u6C34\u6E90\u4E00\u7EA7\u4FDD\u62A4\u533A\u3001\u81EA\u7136\u4FDD\u62A4\u533A\u6838\u5FC3\u533A\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u6709\u653E\u5C04\u6027\u7684\u5E9F\u7269\u3001\u542B\u4F20\u67D3\u75C5\u75C5\u539F\u4F53\u7684\u5E9F\u7269\u3001\u6709\u6BD2\u7269\u8D28\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u975E\u6CD5\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u5371\u9669\u5E9F\u7269\u4E09\u5428\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u542B\u94C5\u3001\u6C5E\u3001\u9549\u3001\u94EC\u3001\u7837\u3001\u94CA\u3001\u9511\u7684\u6C61\u67D3\u7269\uFF0C\u8D85\u8FC7\u56FD\u5BB6\u6216\u8005\u5730\u65B9\u6C61\u67D3\u7269\u6392\u653E\u6807\u51C6\u4E09\u500D\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u542B\u954D\u3001\u94DC\u3001\u950C\u3001\u94F6\u3001\u9492\u3001\u9530\u3001\u94B4\u7684\u6C61\u67D3\u7269\uFF0C\u8D85\u8FC7\u56FD\u5BB6\u6216\u8005\u5730\u65B9\u6C61\u67D3\u7269\u6392\u653E\u6807\u51C6\u5341\u500D\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E94\uFF09\u901A\u8FC7\u6697\u7BA1\u3001\u6E17\u4E95\u3001\u6E17\u5751\u3001\u88C2\u9699\u3001\u6EB6\u6D1E\u3001\u704C\u6CE8\u7B49\u9003\u907F\u76D1\u7BA1\u7684\u65B9\u5F0F\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u6709\u653E\u5C04\u6027\u7684\u5E9F\u7269\u3001\u542B\u4F20\u67D3\u75C5\u75C5\u539F\u4F53\u7684\u5E9F\u7269\u3001\u6709\u6BD2\u7269\u8D28\u7684\uFF1B<br> \u2003\u2003\uFF08\u516D\uFF09\u4E8C\u5E74\u5185\u66FE\u56E0\u8FDD\u53CD\u56FD\u5BB6\u89C4\u5B9A\uFF0C\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u6709\u653E\u5C04\u6027\u7684\u5E9F\u7269\u3001\u542B\u4F20\u67D3\u75C5\u75C5\u539F\u4F53\u7684\u5E9F\u7269\u3001\u6709\u6BD2\u7269\u8D28\u53D7\u8FC7\u4E24\u6B21\u4EE5\u4E0A\u884C\u653F\u5904\u7F5A\uFF0C\u53C8\u5B9E\u65BD\u524D\u5217\u884C\u4E3A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E03\uFF09\u91CD\u70B9\u6392\u6C61\u5355\u4F4D\u7BE1\u6539\u3001\u4F2A\u9020\u81EA\u52A8\u76D1\u6D4B\u6570\u636E\u6216\u8005\u5E72\u6270\u81EA\u52A8\u76D1\u6D4B\u8BBE\u65BD\uFF0C\u6392\u653E\u5316\u5B66\u9700\u6C27\u91CF\u3001\u6C28\u6C2E\u3001\u4E8C\u6C27\u5316\u786B\u3001\u6C2E\u6C27\u5316\u7269\u7B49\u6C61\u67D3\u7269\u7684\uFF1B<br> \u2003\u2003\uFF08\u516B\uFF09\u8FDD\u6CD5\u51CF\u5C11\u9632\u6CBB\u6C61\u67D3\u8BBE\u65BD\u8FD0\u884C\u652F\u51FA\u4E00\u767E\u4E07\u5143\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E5D\uFF09\u8FDD\u6CD5\u6240\u5F97\u6216\u8005\u81F4\u4F7F\u516C\u79C1\u8D22\u4EA7\u635F\u5931\u4E09\u5341\u4E07\u5143\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\uFF09\u9020\u6210\u751F\u6001\u73AF\u5883\u4E25\u91CD\u635F\u5BB3\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E00\uFF09\u81F4\u4F7F\u4E61\u9547\u4EE5\u4E0A\u96C6\u4E2D\u5F0F\u996E\u7528\u6C34\u6C34\u6E90\u53D6\u6C34\u4E2D\u65AD\u5341\u4E8C\u5C0F\u65F6\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E8C\uFF09\u81F4\u4F7F\u57FA\u672C\u519C\u7530\u3001\u9632\u62A4\u6797\u5730\u3001\u7279\u79CD\u7528\u9014\u6797\u5730\u4E94\u4EA9\u4EE5\u4E0A\uFF0C\u5176\u4ED6\u519C\u7528\u5730\u5341\u4EA9\u4EE5\u4E0A\uFF0C\u5176\u4ED6\u571F\u5730\u4E8C\u5341\u4EA9\u4EE5\u4E0A\u57FA\u672C\u529F\u80FD\u4E27\u5931\u6216\u8005\u906D\u53D7\u6C38\u4E45\u6027\u7834\u574F\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E09\uFF09\u81F4\u4F7F\u68EE\u6797\u6216\u8005\u5176\u4ED6\u6797\u6728\u6B7B\u4EA1\u4E94\u5341\u7ACB\u65B9\u7C73\u4EE5\u4E0A\uFF0C\u6216\u8005\u5E7C\u6811\u6B7B\u4EA1\u4E8C\u5343\u4E94\u767E\u682A\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u56DB\uFF09\u81F4\u4F7F\u758F\u6563\u3001\u8F6C\u79FB\u7FA4\u4F175000\u4EBA\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E94\uFF09\u81F4\u4F7F\u4E09\u5341\u4EBA\u4EE5\u4E0A\u4E2D\u6BD2\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u516D\uFF09\u81F4\u4F7F\u4E09\u4EBA\u4EE5\u4E0A\u8F7B\u4F24\u3001\u8F7B\u5EA6\u6B8B\u75BE\u6216\u8005\u5668\u5B98\u7EC4\u7EC7\u635F\u4F24\u5BFC\u81F4\u4E00\u822C\u529F\u80FD\u969C\u788D\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E03\uFF09\u81F4\u4F7F\u4E00\u4EBA\u4EE5\u4E0A\u91CD\u4F24\u3001\u4E2D\u5EA6\u6B8B\u75BE\u6216\u8005\u5668\u5B98\u7EC4\u7EC7\u635F\u4F24\u5BFC\u81F4\u4E25\u91CD\u529F\u80FD\u969C\u788D\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u516B\uFF09\u5176\u4ED6\u4E25\u91CD\u6C61\u67D3\u73AF\u5883\u7684\u60C5\u5F62\u3002</p><p id="t2">\u2003\u2003<b>\u7B2C\u4E8C\u6761</b>\u2003\u5B9E\u65BD\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u4E5D\u6761\u3001\u7B2C\u56DB\u767E\u96F6\u516B\u6761\u89C4\u5B9A\u7684\u884C\u4E3A\uFF0C\u81F4\u4F7F\u516C\u79C1\u8D22\u4EA7\u635F\u5931\u4E09\u5341\u4E07\u5143\u4EE5\u4E0A\uFF0C\u6216\u8005\u5177\u6709\u672C\u89E3\u91CA\u7B2C\u4E00\u6761\u7B2C\u5341\u9879\u81F3\u7B2C\u5341\u4E03\u9879\u89C4\u5B9A\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u5E94\u5F53\u8BA4\u5B9A\u4E3A\u201C\u81F4\u4F7F\u516C\u79C1\u8D22\u4EA7\u906D\u53D7\u91CD\u5927\u635F\u5931\u6216\u8005\u4E25\u91CD\u5371\u5BB3\u4EBA\u4F53\u5065\u5EB7\u201D\u6216\u8005\u201C\u81F4\u4F7F\u516C\u79C1\u8D22\u4EA7\u906D\u53D7\u91CD\u5927\u635F\u5931\u6216\u8005\u9020\u6210\u4EBA\u8EAB\u4F24\u4EA1\u7684\u4E25\u91CD\u540E\u679C\u201D\u3002</p><p id="t3">\u2003\u2003<b>\u7B2C\u4E09\u6761</b>\u2003\u5B9E\u65BD\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u3001\u7B2C\u4E09\u767E\u4E09\u5341\u4E5D\u6761\u89C4\u5B9A\u7684\u884C\u4E3A\uFF0C\u5177\u6709\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u5E94\u5F53\u8BA4\u5B9A\u4E3A\u201C\u540E\u679C\u7279\u522B\u4E25\u91CD\u201D\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u81F4\u4F7F\u53BF\u7EA7\u4EE5\u4E0A\u57CE\u533A\u96C6\u4E2D\u5F0F\u996E\u7528\u6C34\u6C34\u6E90\u53D6\u6C34\u4E2D\u65AD\u5341\u4E8C\u5C0F\u65F6\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u975E\u6CD5\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u5371\u9669\u5E9F\u7269\u4E00\u767E\u5428\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u81F4\u4F7F\u57FA\u672C\u519C\u7530\u3001\u9632\u62A4\u6797\u5730\u3001\u7279\u79CD\u7528\u9014\u6797\u5730\u5341\u4E94\u4EA9\u4EE5\u4E0A\uFF0C\u5176\u4ED6\u519C\u7528\u5730\u4E09\u5341\u4EA9\u4EE5\u4E0A\uFF0C\u5176\u4ED6\u571F\u5730\u516D\u5341\u4EA9\u4EE5\u4E0A\u57FA\u672C\u529F\u80FD\u4E27\u5931\u6216\u8005\u906D\u53D7\u6C38\u4E45\u6027\u7834\u574F\u7684\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09\u81F4\u4F7F\u68EE\u6797\u6216\u8005\u5176\u4ED6\u6797\u6728\u6B7B\u4EA1\u4E00\u767E\u4E94\u5341\u7ACB\u65B9\u7C73\u4EE5\u4E0A\uFF0C\u6216\u8005\u5E7C\u6811\u6B7B\u4EA1\u4E03\u5343\u4E94\u767E\u682A\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E94\uFF09\u81F4\u4F7F\u516C\u79C1\u8D22\u4EA7\u635F\u5931\u4E00\u767E\u4E07\u5143\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u516D\uFF09\u9020\u6210\u751F\u6001\u73AF\u5883\u7279\u522B\u4E25\u91CD\u635F\u5BB3\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E03\uFF09\u81F4\u4F7F\u758F\u6563\u3001\u8F6C\u79FB\u7FA4\u4F1715000\u4EBA\u4EE5\u4E0A\u7684\uFF1B<br> \u2003\u2003\uFF08\u516B\uFF09\u81F4\u4F7F\u4E00\u767E\u4EBA\u4EE5\u4E0A\u4E2D\u6BD2\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E5D\uFF09\u81F4\u4F7F\u5341\u4EBA\u4EE5\u4E0A\u8F7B\u4F24\u3001\u8F7B\u5EA6\u6B8B\u75BE\u6216\u8005\u5668\u5B98\u7EC4\u7EC7\u635F\u4F24\u5BFC\u81F4\u4E00\u822C\u529F\u80FD\u969C\u788D\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\uFF09\u81F4\u4F7F\u4E09\u4EBA\u4EE5\u4E0A\u91CD\u4F24\u3001\u4E2D\u5EA6\u6B8B\u75BE\u6216\u8005\u5668\u5B98\u7EC4\u7EC7\u635F\u4F24\u5BFC\u81F4\u4E25\u91CD\u529F\u80FD\u969C\u788D\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E00\uFF09\u81F4\u4F7F\u4E00\u4EBA\u4EE5\u4E0A\u91CD\u4F24\u3001\u4E2D\u5EA6\u6B8B\u75BE\u6216\u8005\u5668\u5B98\u7EC4\u7EC7\u635F\u4F24\u5BFC\u81F4\u4E25\u91CD\u529F\u80FD\u969C\u788D\uFF0C\u5E76\u81F4\u4F7F\u4E94\u4EBA\u4EE5\u4E0A\u8F7B\u4F24\u3001\u8F7B\u5EA6\u6B8B\u75BE\u6216\u8005\u5668\u5B98\u7EC4\u7EC7\u635F\u4F24\u5BFC\u81F4\u4E00\u822C\u529F\u80FD\u969C\u788D\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E8C\uFF09\u81F4\u4F7F\u4E00\u4EBA\u4EE5\u4E0A\u6B7B\u4EA1\u6216\u8005\u91CD\u5EA6\u6B8B\u75BE\u7684\uFF1B<br> \u2003\u2003\uFF08\u5341\u4E09\uFF09\u5176\u4ED6\u540E\u679C\u7279\u522B\u4E25\u91CD\u7684\u60C5\u5F62\u3002</p><p id="t4">\u2003\u2003<b>\u7B2C\u56DB\u6761</b>\u2003\u5B9E\u65BD\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u3001\u7B2C\u4E09\u767E\u4E09\u5341\u4E5D\u6761\u89C4\u5B9A\u7684\u72AF\u7F6A\u884C\u4E3A\uFF0C\u5177\u6709\u4E0B\u5217\u60C5\u5F62\u4E4B\u4E00\u7684\uFF0C\u5E94\u5F53\u4ECE\u91CD\u5904\u7F5A\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u963B\u6320\u73AF\u5883\u76D1\u7763\u68C0\u67E5\u6216\u8005\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u8C03\u67E5\uFF0C\u5C1A\u4E0D\u6784\u6210\u59A8\u5BB3\u516C\u52A1\u7B49\u72AF\u7F6A\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u5728\u533B\u9662\u3001\u5B66\u6821\u3001\u5C45\u6C11\u533A\u7B49\u4EBA\u53E3\u96C6\u4E2D\u5730\u533A\u53CA\u5176\u9644\u8FD1\uFF0C\u8FDD\u53CD\u56FD\u5BB6\u89C4\u5B9A\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u6709\u653E\u5C04\u6027\u7684\u5E9F\u7269\u3001\u542B\u4F20\u67D3\u75C5\u75C5\u539F\u4F53\u7684\u5E9F\u7269\u3001\u6709\u6BD2\u7269\u8D28\u6216\u8005\u5176\u4ED6\u6709\u5BB3\u7269\u8D28\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u5728\u91CD\u6C61\u67D3\u5929\u6C14\u9884\u8B66\u671F\u95F4\u3001\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u5904\u7F6E\u671F\u95F4\u6216\u8005\u88AB\u8D23\u4EE4\u9650\u671F\u6574\u6539\u671F\u95F4\uFF0C\u8FDD\u53CD\u56FD\u5BB6\u89C4\u5B9A\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u6709\u653E\u5C04\u6027\u7684\u5E9F\u7269\u3001\u542B\u4F20\u67D3\u75C5\u75C5\u539F\u4F53\u7684\u5E9F\u7269\u3001\u6709\u6BD2\u7269\u8D28\u6216\u8005\u5176\u4ED6\u6709\u5BB3\u7269\u8D28\u7684\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09\u5177\u6709\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u8BB8\u53EF\u8BC1\u7684\u4F01\u4E1A\u8FDD\u53CD\u56FD\u5BB6\u89C4\u5B9A\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u6709\u653E\u5C04\u6027\u7684\u5E9F\u7269\u3001\u542B\u4F20\u67D3\u75C5\u75C5\u539F\u4F53\u7684\u5E9F\u7269\u3001\u6709\u6BD2\u7269\u8D28\u6216\u8005\u5176\u4ED6\u6709\u5BB3\u7269\u8D28\u7684\u3002</p><p id="t5">\u2003\u2003<b>\u7B2C\u4E94\u6761</b>\u2003\u5B9E\u65BD\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u3001\u7B2C\u4E09\u767E\u4E09\u5341\u4E5D\u6761\u89C4\u5B9A\u7684\u884C\u4E3A\uFF0C\u521A\u8FBE\u5230\u5E94\u5F53\u8FFD\u7A76\u5211\u4E8B\u8D23\u4EFB\u7684\u6807\u51C6\uFF0C\u4F46\u884C\u4E3A\u4EBA\u53CA\u65F6\u91C7\u53D6\u63AA\u65BD\uFF0C\u9632\u6B62\u635F\u5931\u6269\u5927\u3001\u6D88\u9664\u6C61\u67D3\uFF0C\u5168\u90E8\u8D54\u507F\u635F\u5931\uFF0C\u79EF\u6781\u4FEE\u590D\u751F\u6001\u73AF\u5883\uFF0C\u4E14\u7CFB\u521D\u72AF\uFF0C\u786E\u6709\u6094\u7F6A\u8868\u73B0\u7684\uFF0C\u53EF\u4EE5\u8BA4\u5B9A\u4E3A\u60C5\u8282\u8F7B\u5FAE\uFF0C\u4E0D\u8D77\u8BC9\u6216\u8005\u514D\u4E88\u5211\u4E8B\u5904\u7F5A\uFF1B\u786E\u6709\u5FC5\u8981\u5224\u5904\u5211\u7F5A\u7684\uFF0C\u5E94\u5F53\u4ECE\u5BBD\u5904\u7F5A\u3002</p><p id="t6">\u2003\u2003<b>\u7B2C\u516D\u6761</b>\u2003\u65E0\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u8BB8\u53EF\u8BC1\u4ECE\u4E8B\u6536\u96C6\u3001\u8D2E\u5B58\u3001\u5229\u7528\u3001\u5904\u7F6E\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u6D3B\u52A8\uFF0C\u4E25\u91CD\u6C61\u67D3\u73AF\u5883\u7684\uFF0C\u6309\u7167\u6C61\u67D3\u73AF\u5883\u7F6A\u5B9A\u7F6A\u5904\u7F5A\uFF1B\u540C\u65F6\u6784\u6210\u975E\u6CD5\u7ECF\u8425\u7F6A\u7684\uFF0C\u4F9D\u7167\u5904\u7F5A\u8F83\u91CD\u7684\u89C4\u5B9A\u5B9A\u7F6A\u5904\u7F5A\u3002<br> \u2003\u2003\u5B9E\u65BD\u524D\u6B3E\u89C4\u5B9A\u7684\u884C\u4E3A\uFF0C\u4E0D\u5177\u6709\u8D85\u6807\u6392\u653E\u6C61\u67D3\u7269\u3001\u975E\u6CD5\u503E\u5012\u6C61\u67D3\u7269\u6216\u8005\u5176\u4ED6\u8FDD\u6CD5\u9020\u6210\u73AF\u5883\u6C61\u67D3\u7684\u60C5\u5F62\u7684\uFF0C\u53EF\u4EE5\u8BA4\u5B9A\u4E3A\u975E\u6CD5\u7ECF\u8425\u60C5\u8282\u663E\u8457\u8F7B\u5FAE\u5371\u5BB3\u4E0D\u5927\uFF0C\u4E0D\u8BA4\u4E3A\u662F\u72AF\u7F6A\uFF1B\u6784\u6210\u751F\u4EA7\u3001\u9500\u552E\u4F2A\u52A3\u4EA7\u54C1\u7B49\u5176\u4ED6\u72AF\u7F6A\u7684\uFF0C\u4EE5\u5176\u4ED6\u72AF\u7F6A\u8BBA\u5904\u3002</p><p id="t7">\u2003\u2003<b>\u7B2C\u4E03\u6761</b>\u2003\u660E\u77E5\u4ED6\u4EBA\u65E0\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u8BB8\u53EF\u8BC1\uFF0C\u5411\u5176\u63D0\u4F9B\u6216\u8005\u59D4\u6258\u5176\u6536\u96C6\u3001\u8D2E\u5B58\u3001\u5229\u7528\u3001\u5904\u7F6E\u5371\u9669\u5E9F\u7269\uFF0C\u4E25\u91CD\u6C61\u67D3\u73AF\u5883\u7684\uFF0C\u4EE5\u5171\u540C\u72AF\u7F6A\u8BBA\u5904\u3002</p><p id="t8">\u2003\u2003<b>\u7B2C\u516B\u6761</b>\u2003\u8FDD\u53CD\u56FD\u5BB6\u89C4\u5B9A\uFF0C\u6392\u653E\u3001\u503E\u5012\u3001\u5904\u7F6E\u542B\u6709\u6BD2\u5BB3\u6027\u3001\u653E\u5C04\u6027\u3001\u4F20\u67D3\u75C5\u75C5\u539F\u4F53\u7B49\u7269\u8D28\u7684\u6C61\u67D3\u7269\uFF0C\u540C\u65F6\u6784\u6210\u6C61\u67D3\u73AF\u5883\u7F6A\u3001\u975E\u6CD5\u5904\u7F6E\u8FDB\u53E3\u7684\u56FA\u4F53\u5E9F\u7269\u7F6A\u3001\u6295\u653E\u5371\u9669\u7269\u8D28\u7F6A\u7B49\u72AF\u7F6A\u7684\uFF0C\u4F9D\u7167\u5904\u7F5A\u8F83\u91CD\u7684\u89C4\u5B9A\u5B9A\u7F6A\u5904\u7F5A\u3002</p><p id="t9">\u2003\u2003<b>\u7B2C\u4E5D\u6761</b>\u2003\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u673A\u6784\u6216\u5176\u4EBA\u5458\uFF0C\u6545\u610F\u63D0\u4F9B\u865A\u5047\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\uFF0C\u60C5\u8282\u4E25\u91CD\u7684\uFF0C\u6216\u8005\u4E25\u91CD\u4E0D\u8D1F\u8D23\u4EFB\uFF0C\u51FA\u5177\u7684\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u5B58\u5728\u91CD\u5927\u5931\u5B9E\uFF0C\u9020\u6210\u4E25\u91CD\u540E\u679C\u7684\uFF0C\u5E94\u5F53\u4F9D\u7167\u5211\u6CD5\u7B2C\u4E8C\u767E\u4E8C\u5341\u4E5D\u6761\u3001\u7B2C\u4E8C\u767E\u4E09\u5341\u4E00\u6761\u7684\u89C4\u5B9A\uFF0C\u4EE5\u63D0\u4F9B\u865A\u5047\u8BC1\u660E\u6587\u4EF6\u7F6A\u6216\u8005\u51FA\u5177\u8BC1\u660E\u6587\u4EF6\u91CD\u5927\u5931\u5B9E\u7F6A\u5B9A\u7F6A\u5904\u7F5A\u3002</p><p id="t10">\u2003\u2003<b>\u7B2C\u5341\u6761</b>\u2003\u8FDD\u53CD\u56FD\u5BB6\u89C4\u5B9A\uFF0C\u9488\u5BF9\u73AF\u5883\u8D28\u91CF\u76D1\u6D4B\u7CFB\u7EDF\u5B9E\u65BD\u4E0B\u5217\u884C\u4E3A\uFF0C\u6216\u8005\u5F3A\u4EE4\u3001\u6307\u4F7F\u3001\u6388\u610F\u4ED6\u4EBA\u5B9E\u65BD\u4E0B\u5217\u884C\u4E3A\u7684\uFF0C\u5E94\u5F53\u4F9D\u7167\u5211\u6CD5\u7B2C\u4E8C\u767E\u516B\u5341\u516D\u6761\u7684\u89C4\u5B9A\uFF0C\u4EE5\u7834\u574F\u8BA1\u7B97\u673A\u4FE1\u606F\u7CFB\u7EDF\u7F6A\u8BBA\u5904\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u4FEE\u6539\u53C2\u6570\u6216\u8005\u76D1\u6D4B\u6570\u636E\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u5E72\u6270\u91C7\u6837\uFF0C\u81F4\u4F7F\u76D1\u6D4B\u6570\u636E\u4E25\u91CD\u5931\u771F\u7684\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u5176\u4ED6\u7834\u574F\u73AF\u5883\u8D28\u91CF\u76D1\u6D4B\u7CFB\u7EDF\u7684\u884C\u4E3A\u3002<br> \u2003\u2003\u91CD\u70B9\u6392\u6C61\u5355\u4F4D\u7BE1\u6539\u3001\u4F2A\u9020\u81EA\u52A8\u76D1\u6D4B\u6570\u636E\u6216\u8005\u5E72\u6270\u81EA\u52A8\u76D1\u6D4B\u8BBE\u65BD\uFF0C\u6392\u653E\u5316\u5B66\u9700\u6C27\u91CF\u3001\u6C28\u6C2E\u3001\u4E8C\u6C27\u5316\u786B\u3001\u6C2E\u6C27\u5316\u7269\u7B49\u6C61\u67D3\u7269\uFF0C\u540C\u65F6\u6784\u6210\u6C61\u67D3\u73AF\u5883\u7F6A\u548C\u7834\u574F\u8BA1\u7B97\u673A\u4FE1\u606F\u7CFB\u7EDF\u7F6A\u7684\uFF0C\u4F9D\u7167\u5904\u7F5A\u8F83\u91CD\u7684\u89C4\u5B9A\u5B9A\u7F6A\u5904\u7F5A\u3002<br> \u2003\u2003\u4ECE\u4E8B\u73AF\u5883\u76D1\u6D4B\u8BBE\u65BD\u7EF4\u62A4\u3001\u8FD0\u8425\u7684\u4EBA\u5458\u5B9E\u65BD\u6216\u8005\u53C2\u4E0E\u5B9E\u65BD\u7BE1\u6539\u3001\u4F2A\u9020\u81EA\u52A8\u76D1\u6D4B\u6570\u636E\u3001\u5E72\u6270\u81EA\u52A8\u76D1\u6D4B\u8BBE\u65BD\u3001\u7834\u574F\u73AF\u5883\u8D28\u91CF\u76D1\u6D4B\u7CFB\u7EDF\u7B49\u884C\u4E3A\u7684\uFF0C\u5E94\u5F53\u4ECE\u91CD\u5904\u7F5A\u3002</p><p id="t11">\u2003\u2003<b>\u7B2C\u5341\u4E00\u6761</b>\u2003\u5355\u4F4D\u5B9E\u65BD\u672C\u89E3\u91CA\u89C4\u5B9A\u7684\u72AF\u7F6A\u7684\uFF0C\u4F9D\u7167\u672C\u89E3\u91CA\u89C4\u5B9A\u7684\u5B9A\u7F6A\u91CF\u5211\u6807\u51C6\uFF0C\u5BF9\u76F4\u63A5\u8D1F\u8D23\u7684\u4E3B\u7BA1\u4EBA\u5458\u548C\u5176\u4ED6\u76F4\u63A5\u8D23\u4EFB\u4EBA\u5458\u5B9A\u7F6A\u5904\u7F5A\uFF0C\u5E76\u5BF9\u5355\u4F4D\u5224\u5904\u7F5A\u91D1\u3002</p><p id="t12">\u2003\u2003<b>\u7B2C\u5341\u4E8C\u6761</b>\u2003\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u53CA\u5176\u6240\u5C5E\u76D1\u6D4B\u673A\u6784\u5728\u884C\u653F\u6267\u6CD5\u8FC7\u7A0B\u4E2D\u6536\u96C6\u7684\u76D1\u6D4B\u6570\u636E\uFF0C\u5728\u5211\u4E8B\u8BC9\u8BBC\u4E2D\u53EF\u4EE5\u4F5C\u4E3A\u8BC1\u636E\u4F7F\u7528\u3002<br> \u2003\u2003\u516C\u5B89\u673A\u5173\u5355\u72EC\u6216\u8005\u4F1A\u540C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\uFF0C\u63D0\u53D6\u6C61\u67D3\u7269\u6837\u54C1\u8FDB\u884C\u68C0\u6D4B\u83B7\u53D6\u7684\u6570\u636E\uFF0C\u5728\u5211\u4E8B\u8BC9\u8BBC\u4E2D\u53EF\u4EE5\u4F5C\u4E3A\u8BC1\u636E\u4F7F\u7528\u3002</p><p id="t13">\u2003\u2003<b>\u7B2C\u5341\u4E09\u6761</b>\u2003\u5BF9\u56FD\u5BB6\u5371\u9669\u5E9F\u7269\u540D\u5F55\u6240\u5217\u7684\u5E9F\u7269\uFF0C\u53EF\u4EE5\u4F9D\u636E\u6D89\u6848\u7269\u8D28\u7684\u6765\u6E90\u3001\u4EA7\u751F\u8FC7\u7A0B\u3001\u88AB\u544A\u4EBA\u4F9B\u8FF0\u3001\u8BC1\u4EBA\u8BC1\u8A00\u4EE5\u53CA\u7ECF\u6279\u51C6\u6216\u8005\u5907\u6848\u7684\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u7B49\u8BC1\u636E\uFF0C\u7ED3\u5408\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u3001\u516C\u5B89\u673A\u5173\u7B49\u51FA\u5177\u7684\u4E66\u9762\u610F\u89C1\u4F5C\u51FA\u8BA4\u5B9A\u3002<br> \u2003\u2003\u5BF9\u4E8E\u5371\u9669\u5E9F\u7269\u7684\u6570\u91CF\uFF0C\u53EF\u4EE5\u7EFC\u5408\u88AB\u544A\u4EBA\u4F9B\u8FF0\uFF0C\u6D89\u6848\u4F01\u4E1A\u7684\u751F\u4EA7\u5DE5\u827A\u3001\u7269\u8017\u3001\u80FD\u8017\u60C5\u51B5\uFF0C\u4EE5\u53CA\u7ECF\u6279\u51C6\u6216\u8005\u5907\u6848\u7684\u73AF\u5883\u5F71\u54CD\u8BC4\u4EF7\u6587\u4EF6\u7B49\u8BC1\u636E\u4F5C\u51FA\u8BA4\u5B9A\u3002</p><p id="t14">\u2003\u2003<b>\u7B2C\u5341\u56DB\u6761</b>\u2003\u5BF9\u6848\u4EF6\u6240\u6D89\u7684\u73AF\u5883\u6C61\u67D3\u4E13\u95E8\u6027\u95EE\u9898\u96BE\u4EE5\u786E\u5B9A\u7684\uFF0C\u4F9D\u636E\u53F8\u6CD5\u9274\u5B9A\u673A\u6784\u51FA\u5177\u7684\u9274\u5B9A\u610F\u89C1\uFF0C\u6216\u8005\u56FD\u52A1\u9662\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u3001\u516C\u5B89\u90E8\u95E8\u6307\u5B9A\u7684\u673A\u6784\u51FA\u5177\u7684\u62A5\u544A\uFF0C\u7ED3\u5408\u5176\u4ED6\u8BC1\u636E\u4F5C\u51FA\u8BA4\u5B9A\u3002</p><p id="t15">\u2003\u2003<b>\u7B2C\u5341\u4E94\u6761</b>\u2003\u4E0B\u5217\u7269\u8D28\u5E94\u5F53\u8BA4\u5B9A\u4E3A\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u89C4\u5B9A\u7684\u201C\u6709\u6BD2\u7269\u8D28\u201D\uFF1A<br> \u2003\u2003\uFF08\u4E00\uFF09\u5371\u9669\u5E9F\u7269\uFF0C\u662F\u6307\u5217\u5165\u56FD\u5BB6\u5371\u9669\u5E9F\u7269\u540D\u5F55\uFF0C\u6216\u8005\u6839\u636E\u56FD\u5BB6\u89C4\u5B9A\u7684\u5371\u9669\u5E9F\u7269\u9274\u522B\u6807\u51C6\u548C\u9274\u522B\u65B9\u6CD5\u8BA4\u5B9A\u7684\uFF0C\u5177\u6709\u5371\u9669\u7279\u6027\u7684\u5E9F\u7269\uFF1B<br> \u2003\u2003\uFF08\u4E8C\uFF09\u300A\u5173\u4E8E\u6301\u4E45\u6027\u6709\u673A\u6C61\u67D3\u7269\u7684\u65AF\u5FB7\u54E5\u5C14\u6469\u516C\u7EA6\u300B\u9644\u4EF6\u6240\u5217\u7269\u8D28\uFF1B<br> \u2003\u2003\uFF08\u4E09\uFF09\u542B\u91CD\u91D1\u5C5E\u7684\u6C61\u67D3\u7269\uFF1B<br> \u2003\u2003\uFF08\u56DB\uFF09\u5176\u4ED6\u5177\u6709\u6BD2\u6027\uFF0C\u53EF\u80FD\u6C61\u67D3\u73AF\u5883\u7684\u7269\u8D28\u3002</p><p id="t16">\u2003\u2003<b>\u7B2C\u5341\u516D\u6761</b>\u2003\u65E0\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u8BB8\u53EF\u8BC1\uFF0C\u4EE5\u8425\u5229\u4E3A\u76EE\u7684\uFF0C\u4ECE\u5371\u9669\u5E9F\u7269\u4E2D\u63D0\u53D6\u7269\u8D28\u4F5C\u4E3A\u539F\u6750\u6599\u6216\u8005\u71C3\u6599\uFF0C\u5E76\u5177\u6709\u8D85\u6807\u6392\u653E\u6C61\u67D3\u7269\u3001\u975E\u6CD5\u503E\u5012\u6C61\u67D3\u7269\u6216\u8005\u5176\u4ED6\u8FDD\u6CD5\u9020\u6210\u73AF\u5883\u6C61\u67D3\u7684\u60C5\u5F62\u7684\u884C\u4E3A\uFF0C\u5E94\u5F53\u8BA4\u5B9A\u4E3A\u201C\u975E\u6CD5\u5904\u7F6E\u5371\u9669\u5E9F\u7269\u201D\u3002</p><p id="t17">\u2003\u2003<b>\u7B2C\u5341\u4E03\u6761</b>\u2003\u672C\u89E3\u91CA\u6240\u79F0\u201C\u4E8C\u5E74\u5185\u201D\uFF0C\u4EE5\u7B2C\u4E00\u6B21\u8FDD\u6CD5\u884C\u4E3A\u53D7\u5230\u884C\u653F\u5904\u7F5A\u7684\u751F\u6548\u4E4B\u65E5\u4E0E\u53C8\u5B9E\u65BD\u76F8\u5E94\u884C\u4E3A\u4E4B\u65E5\u7684\u65F6\u95F4\u95F4\u9694\u8BA1\u7B97\u786E\u5B9A\u3002<br> \u2003\u2003\u672C\u89E3\u91CA\u6240\u79F0\u201C\u91CD\u70B9\u6392\u6C61\u5355\u4F4D\u201D\uFF0C\u662F\u6307\u8BBE\u533A\u7684\u5E02\u7EA7\u4EE5\u4E0A\u4EBA\u6C11\u653F\u5E9C\u73AF\u5883\u4FDD\u62A4\u4E3B\u7BA1\u90E8\u95E8\u4F9D\u6CD5\u786E\u5B9A\u7684\u5E94\u5F53\u5B89\u88C5\u3001\u4F7F\u7528\u6C61\u67D3\u7269\u6392\u653E\u81EA\u52A8\u76D1\u6D4B\u8BBE\u5907\u7684\u91CD\u70B9\u76D1\u63A7\u4F01\u4E1A\u53CA\u5176\u4ED6\u5355\u4F4D\u3002<br> \u2003\u2003\u672C\u89E3\u91CA\u6240\u79F0\u201C\u8FDD\u6CD5\u6240\u5F97\u201D\uFF0C\u662F\u6307\u5B9E\u65BD\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u3001\u7B2C\u4E09\u767E\u4E09\u5341\u4E5D\u6761\u89C4\u5B9A\u7684\u884C\u4E3A\u6240\u5F97\u548C\u53EF\u5F97\u7684\u5168\u90E8\u8FDD\u6CD5\u6536\u5165\u3002<br> \u2003\u2003\u672C\u89E3\u91CA\u6240\u79F0\u201C\u516C\u79C1\u8D22\u4EA7\u635F\u5931\u201D\uFF0C\u5305\u62EC\u5B9E\u65BD\u5211\u6CD5\u7B2C\u4E09\u767E\u4E09\u5341\u516B\u6761\u3001\u7B2C\u4E09\u767E\u4E09\u5341\u4E5D\u6761\u89C4\u5B9A\u7684\u884C\u4E3A\u76F4\u63A5\u9020\u6210\u8D22\u4EA7\u635F\u6BC1\u3001\u51CF\u5C11\u7684\u5B9E\u9645\u4EF7\u503C\uFF0C\u4E3A\u9632\u6B62\u6C61\u67D3\u6269\u5927\u3001\u6D88\u9664\u6C61\u67D3\u800C\u91C7\u53D6\u5FC5\u8981\u5408\u7406\u63AA\u65BD\u6240\u4EA7\u751F\u7684\u8D39\u7528\uFF0C\u4EE5\u53CA\u5904\u7F6E\u7A81\u53D1\u73AF\u5883\u4E8B\u4EF6\u7684\u5E94\u6025\u76D1\u6D4B\u8D39\u7528\u3002<br> \u2003\u2003\u672C\u89E3\u91CA\u6240\u79F0\u201C\u751F\u6001\u73AF\u5883\u635F\u5BB3\u201D\uFF0C\u5305\u62EC\u751F\u6001\u73AF\u5883\u4FEE\u590D\u8D39\u7528\uFF0C\u751F\u6001\u73AF\u5883\u4FEE\u590D\u671F\u95F4\u670D\u52A1\u529F\u80FD\u7684\u635F\u5931\u548C\u751F\u6001\u73AF\u5883\u529F\u80FD\u6C38\u4E45\u6027\u635F\u5BB3\u9020\u6210\u7684\u635F\u5931\uFF0C\u4EE5\u53CA\u5176\u4ED6\u5FC5\u8981\u5408\u7406\u8D39\u7528\u3002<br> \u2003\u2003\u672C\u89E3\u91CA\u6240\u79F0\u201C\u65E0\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u8BB8\u53EF\u8BC1\u201D\uFF0C\u662F\u6307\u672A\u53D6\u5F97\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u8BB8\u53EF\u8BC1\uFF0C\u6216\u8005\u8D85\u51FA\u5371\u9669\u5E9F\u7269\u7ECF\u8425\u8BB8\u53EF\u8BC1\u7684\u7ECF\u8425\u8303\u56F4\u3002</p><p id="t18">\u2003\u2003<b>\u7B2C\u5341\u516B\u6761</b>\u2003\u672C\u89E3\u91CA\u81EA2017\u5E741\u67081\u65E5\u8D77\u65BD\u884C\u3002\u672C\u89E3\u91CA\u65BD\u884C\u540E\uFF0C\u300A\u6700\u9AD8\u4EBA\u6C11\u6CD5\u9662\u3001\u6700\u9AD8\u4EBA\u6C11\u68C0\u5BDF\u9662\u5173\u4E8E\u529E\u7406\u73AF\u5883\u6C61\u67D3\u5211\u4E8B\u6848\u4EF6\u9002\u7528\u6CD5\u5F8B\u82E5\u5E72\u95EE\u9898\u7684\u89E3\u91CA\u300B\uFF08\u6CD5\u91CA\u30142013\u301515\u53F7\uFF09\u540C\u65F6\u5E9F\u6B62\uFF1B\u4E4B\u524D\u53D1\u5E03\u7684\u53F8\u6CD5\u89E3\u91CA\u4E0E\u672C\u89E3\u91CA\u4E0D\u4E00\u81F4\u7684\uFF0C\u4EE5\u672C\u89E3\u91CA\u4E3A\u51C6\u3002</p><hr>',22),d={class:"custom-container warning"},l=r("p",{class:"custom-container-title"},"\u539F\u6587\u94FE\u63A5",-1),h={href:"https://www.court.gov.cn/zixun-xiangqing-33781.html",target:"_blank",rel:"noopener noreferrer"},f=s("https://www.court.gov.cn/zixun-xiangqing-33781.html");function m(_,u){const t=n("ExternalLinkIcon");return a(),e("div",null,[c,r("div",d,[l,r("p",null,[r("a",h,[f,p(t)])])])])}const w=b(o,[["render",m],["__file","zgrmfyzgrmjcygyblhjwrxsajsyflrgwtdjs.html.vue"]]);export{w as default};
